import type { Identifier, RaRecord } from "ra-core";
import type { ComponentType } from "react";

import type {
  COMPANY_CREATED,
  CONTACT_CREATED,
  CONTACT_NOTE_CREATED,
  DEAL_CREATED,
  DEAL_NOTE_CREATED,
  QUOTE_CREATED,
  QUOTE_UPDATED,
  TASK_CREATED,
  TASK_COMPLETED,
  DEAL_STATUS_CHANGED,
  DEAL_ARCHIVED,
  DEAL_UNARCHIVED,
  NOTE_DELETED,
  QUOTE_DELETED,
  TASK_DELETED,
  APPOINTMENT_CREATED,
  APPOINTMENT_DELETED,
} from "./consts";

export type SignUpData = {
  email: string;
  password: string;
  first_name: string;
  last_name: string;
};

export type SalesFormData = {
  avatar: string;
  email: string;
  password?: string;
  first_name: string;
  last_name: string;
  administrator: boolean;
  disabled: boolean;
};

export type Sale = {
  first_name: string;
  last_name: string;
  administrator: boolean;
  avatar?: RAFile;
  disabled?: boolean;
  user_id: string;

  /**
   * This is a copy of the user's email, to make it easier to handle by react admin
   * DO NOT UPDATE this field directly, it should be updated by the backend
   */
  email: string;

  /**
   * This is used by the fake rest provider to store the password
   * DO NOT USE this field in your code besides the fake rest provider
   * @deprecated
   */
  password?: string;
} & Pick<RaRecord, "id">;

export type Service = {
  name: string;
  color?: string | null;
  created_at: string;
} & Pick<RaRecord, "id">;

export type Company = {
  name: string;
  logo: RAFile;
  sector: string;
  size: 1 | 10 | 50 | 250 | 500;
  linkedin_url: string;
  website: string;
  phone_number: string;
  address: string;
  zipcode: string;
  city: string;
  stateAbbr: string;
  sales_id: Identifier;
  created_at: string;
  description: string;
  revenue: string;
  tax_identifier: string;
  country: string;
  context_links?: string[];
  nb_contacts?: number;
  nb_deals?: number;
  // B2C fields for clients
  services?: Identifier[];
  nb_tasks?: number;
} & Pick<RaRecord, "id">;

export type EmailAndType = {
  email: string;
  type: "Work" | "Home" | "Other";
};

export type PhoneNumberAndType = {
  number: string;
  type: "Work" | "Home" | "Other";
};

export type Contact = {
  first_name: string;
  last_name: string;
  title: string;
  company_id: Identifier;
  email_jsonb: EmailAndType[];
  avatar?: Partial<RAFile>;
  linkedin_url?: string | null;
  first_seen: string;
  last_seen: string;
  has_newsletter: boolean;
  tags: Identifier[];
  gender: string;
  sales_id: Identifier;
  status: string;
  background: string;
  phone_jsonb: PhoneNumberAndType[];
  nb_tasks?: number;
  company_name?: string;
  archived_at?: string;
  // B2C fields for leads
  flat_villa_number?: string;
  building_street?: string;
  area?: string;
  city?: string;
  google_maps_link?: string;
  latitude?: number | null;
  longitude?: number | null;
  phone_has_whatsapp?: boolean;
  services_interested?: Identifier[];
  description?: string;
} & Pick<RaRecord, "id">;

// Type alias for clarity - Lead is a Contact in B2C context
export type Lead = Contact;

// Type alias for clarity - Client is a Company in B2C context
export type Client = Company;

export type ContactNote = {
  contact_id: Identifier;
  text: string;
  date: string;
  sales_id: Identifier;
  status: string;
  attachments?: AttachmentNote[];
  tagged_user_ids?: Identifier[];
} & Pick<RaRecord, "id">;

export type Deal = {
  name?: string; // Deprecated: use first_name and last_name instead
  first_name?: string;
  last_name?: string;
  company_id: Identifier;
  contact_ids: Identifier[];
  category: string;
  stage: string;
  description: string;
  amount: number;
  created_at: string;
  updated_at: string;
  archived_at?: string;
  expected_closing_date: string;
  sales_id: Identifier;
  index: number;
  // B2C field for lead status
  lead_id?: Identifier;
} & Pick<RaRecord, "id">;

// Type alias for clarity - LeadStatus is a Deal in B2C context
export type LeadStatus = Deal;

export type DealNote = {
  deal_id: Identifier;
  text: string;
  date: string;
  sales_id: Identifier;
  attachments?: AttachmentNote[];
  tagged_user_ids?: Identifier[];

  // This is defined for compatibility with `ContactNote`
  status?: undefined;
} & Pick<RaRecord, "id">;

export type Tag = {
  name: string;
  color: string;
} & Pick<RaRecord, "id">;

export type Task = {
  contact_id: Identifier;
  type: string;
  text: string;
  due_date: string;
  done_date?: string | null;
  sales_id?: Identifier;
  created_at?: string;
  tagged_user_ids?: Identifier[];
} & Pick<RaRecord, "id">;

export type Quote = {
  contact_id: Identifier;
  service_id?: Identifier;
  description?: string;
  amount: number;
  status: "Draft" | "Sent" | "Accepted" | "Rejected";
  created_at: string;
  updated_at: string;
  sales_id?: Identifier;
} & Pick<RaRecord, "id">;

export type ActivityCompanyCreated = {
  type: typeof COMPANY_CREATED;
  company_id: Identifier;
  company: Company;
  sales_id: Identifier;
  date: string;
} & Pick<RaRecord, "id">;

export type ActivityContactCreated = {
  type: typeof CONTACT_CREATED;
  company_id: Identifier;
  sales_id?: Identifier;
  contact: Contact;
  date: string;
} & Pick<RaRecord, "id">;

export type ActivityContactNoteCreated = {
  type: typeof CONTACT_NOTE_CREATED;
  sales_id?: Identifier;
  contactNote: ContactNote;
  date: string;
} & Pick<RaRecord, "id">;

export type ActivityDealCreated = {
  type: typeof DEAL_CREATED;
  company_id: Identifier;
  sales_id?: Identifier;
  deal: Deal;
  date: string;
};

export type ActivityDealNoteCreated = {
  type: typeof DEAL_NOTE_CREATED;
  sales_id?: Identifier;
  dealNote: DealNote;
  date: string;
};

export type ActivityQuoteCreated = {
  type: typeof QUOTE_CREATED;
  sales_id?: Identifier;
  quote: Quote;
  date: string;
} & Pick<RaRecord, "id">;

export type ActivityQuoteUpdated = {
  type: typeof QUOTE_UPDATED;
  sales_id?: Identifier;
  quote: Quote;
  date: string;
} & Pick<RaRecord, "id">;

export type ActivityTaskCreated = {
  type: typeof TASK_CREATED;
  sales_id?: Identifier;
  task: Task;
  date: string;
} & Pick<RaRecord, "id">;

export type ActivityTaskCompleted = {
  type: typeof TASK_COMPLETED;
  sales_id?: Identifier;
  task: Task;
  date: string;
} & Pick<RaRecord, "id">;

export type ActivityDealStatusChanged = {
  type: typeof DEAL_STATUS_CHANGED;
  company_id?: Identifier;
  sales_id?: Identifier;
  deal: Deal;
  date: string;
  old_stage?: string | null;
  new_stage?: string | null;
} & Pick<RaRecord, "id">;

export type ActivityDealArchived = {
  type: typeof DEAL_ARCHIVED;
  company_id?: Identifier;
  sales_id?: Identifier;
  deal: Deal;
  date: string;
} & Pick<RaRecord, "id">;

export type ActivityDealUnarchived = {
  type: typeof DEAL_UNARCHIVED;
  company_id?: Identifier;
  sales_id?: Identifier;
  deal: Deal;
  date: string;
} & Pick<RaRecord, "id">;

export type ActivityNoteDeleted = {
  type: typeof NOTE_DELETED;
  sales_id?: Identifier;
  contact_id?: Identifier;
  deal_id?: Identifier;
  note_text?: string;
  date: string;
} & Pick<RaRecord, "id">;

export type ActivityQuoteDeleted = {
  type: typeof QUOTE_DELETED;
  sales_id?: Identifier;
  contact_id?: Identifier;
  quote_amount?: number;
  quote_description?: string;
  date: string;
} & Pick<RaRecord, "id">;

export type ActivityTaskDeleted = {
  type: typeof TASK_DELETED;
  sales_id?: Identifier;
  contact_id?: Identifier;
  task_text?: string;
  task_type?: string;
  date: string;
} & Pick<RaRecord, "id">;

export type ActivityAppointmentCreated = {
  type: typeof APPOINTMENT_CREATED;
  sales_id?: Identifier;
  contact_id?: Identifier;
  appointment_id?: Identifier;
  appointment?: Appointment;
  date: string;
} & Pick<RaRecord, "id">;

export type ActivityAppointmentDeleted = {
  type: typeof APPOINTMENT_DELETED;
  sales_id?: Identifier;
  contact_id?: Identifier;
  appointment_id?: Identifier;
  appointment_date?: string;
  appointment_type?: string;
  date: string;
} & Pick<RaRecord, "id">;

export type Activity = RaRecord &
  (
    | ActivityCompanyCreated
    | ActivityContactCreated
    | ActivityContactNoteCreated
    | ActivityDealCreated
    | ActivityDealNoteCreated
    | ActivityQuoteCreated
    | ActivityQuoteUpdated
    | ActivityTaskCreated
    | ActivityTaskCompleted
    | ActivityDealStatusChanged
    | ActivityDealArchived
    | ActivityDealUnarchived
    | ActivityNoteDeleted
    | ActivityQuoteDeleted
    | ActivityTaskDeleted
    | ActivityAppointmentCreated
    | ActivityAppointmentDeleted
  );

export interface RAFile {
  src: string;
  title: string;
  path?: string;
  rawFile: File;
  type?: string;
}

export type AttachmentNote = RAFile;
export interface DealStage {
  value: string;
  label: string;
}

export interface NoteStatus {
  value: string;
  label: string;
  color: string;
}

export interface ContactGender {
  value: string;
  label: string;
  icon: ComponentType<{ className?: string }>;
}

export type Staff = {
  first_name: string;
  last_name: string;
  staff_type: string;
  phone: string;
  email?: string;
  created_at: string;
  updated_at: string;
  // Optional fields (kept for backward compatibility with existing data)
  specialization?: string;
  status?: "active" | "inactive";
} & Pick<RaRecord, "id">;

export type RecurrenceConfig = {
  pattern: "daily" | "weekly" | "monthly" | "yearly" | "custom";
  interval: number; // e.g., 2 for "every 2 weeks"
  end_type: "date" | "occurrences";
  end_date?: string | null; // YYYY-MM-DD
  occurrences?: number | null;
  days_of_week?: number[] | null; // For weekly: 0=Sunday, 6=Saturday
  day_of_month?: number | null; // For monthly: 1-31
  week_of_month?: number | null; // For monthly: 1-5 (first Monday, etc.)
  month?: number | null; // For yearly: 1-12
  custom_unit?: "days" | "weeks" | "months" | null; // For custom pattern
};

export type Appointment = {
  patient_id: Identifier;
  appointment_date: string;
  start_time: string;
  end_time: string;
  duration_minutes: number;
  appointment_type: "doctor_on_call" | "lab_test" | "teleconsultation" | "physiotherapy" | "caregiver" | "iv_therapy";
  status: "scheduled" | "confirmed" | "completed" | "cancelled";
  notes?: string;
  mini_notes?: string;
  full_notes?: string;
  pickup_instructions?: string;
  custom_fields?: Record<string, any>;
  primary_staff_id?: Identifier;
  driver_id?: Identifier;
  staff_ids?: Identifier[];
  payment_package_id?: Identifier | null;
  created_at: string;
  updated_at: string;
  // Recurrence fields
  recurrence_id?: string;
  recurrence_config?: RecurrenceConfig;
  is_recurring?: boolean;
  recurrence_sequence?: number;
} & Pick<RaRecord, "id">;

export type AppointmentStaffAssignment = {
  appointment_id: Identifier;
  staff_id: Identifier;
  role: "primary" | "driver" | "other";
  created_at: string;
} & Pick<RaRecord, "id">;

// Payment tracking types
export type PaymentPackage = {
  patient_id: Identifier;
  service_id?: Identifier;
  package_type: "session-based" | "time-based" | "post-payment";
  total_amount: number;
  price_per_hour?: number | null;
  total_sessions?: number | null;
  total_hours?: number | null;
  duration_days?: number | null;
  hours_per_day?: number | null;
  start_date: string; // YYYY-MM-DD
  end_date?: string | null; // YYYY-MM-DD
  renewal_date?: string | null; // YYYY-MM-DD
  status: "active" | "completed" | "expired";
  renewed_from_package_id?: Identifier | null;
  name?: string | null; // Human-readable package name
  created_at: string;
  updated_at: string;
} & Pick<RaRecord, "id">;

export type PaymentTransaction = {
  payment_package_id: Identifier;
  amount_received: number;
  bank_charge: number;
  net_amount: number;
  payment_method: "POS" | "Tabby" | "Payment Link" | "Ziina" | "Cash";
  date_paid: string; // YYYY-MM-DD
  date_received: string; // YYYY-MM-DD
  invoice_number?: string | null;
  installment_number?: number | null;
  created_at: string;
  updated_at: string;
} & Pick<RaRecord, "id">;

export type PackageUsage = {
  payment_package_id: Identifier;
  appointment_id?: Identifier | null;
  usage_type: "session" | "hours";
  quantity_used: number;
  usage_date: string; // YYYY-MM-DD
  is_manual_adjustment: boolean;
  notes?: string | null;
  created_at: string;
} & Pick<RaRecord, "id">;

export type InstallmentSchedule = {
  payment_package_id: Identifier;
  installment_number: number;
  due_date: string; // YYYY-MM-DD
  amount_due: number;
  is_paid: boolean;
  paid_date?: string | null; // YYYY-MM-DD
  reminder_sent: boolean;
  created_at: string;
  updated_at: string;
} & Pick<RaRecord, "id">;

export type PaymentSettings = {
  payment_method: "POS" | "Tabby" | "Payment Link" | "Ziina" | "Cash";
  fee_percentage?: number | null;
  fixed_fee_amount?: number | null;
  vat_percentage: number;
  is_active: boolean;
  created_at: string;
  updated_at: string;
} & Pick<RaRecord, "id">;

export const COMPANY_CREATED = "company.created" as const;
export const CONTACT_CREATED = "contact.created" as const;
export const CONTACT_NOTE_CREATED = "contactNote.created" as const;
export const DEAL_CREATED = "deal.created" as const;
export const DEAL_NOTE_CREATED = "dealNote.created" as const;
export const QUOTE_CREATED = "quote.created" as const;
export const QUOTE_UPDATED = "quote.updated" as const;
export const TASK_CREATED = "task.created" as const;
export const TASK_COMPLETED = "task.completed" as const;
export const DEAL_STATUS_CHANGED = "deal.statusChanged" as const;
export const DEAL_ARCHIVED = "deal.archived" as const;
export const DEAL_UNARCHIVED = "deal.unarchived" as const;
export const NOTE_DELETED = "note.deleted" as const;
export const QUOTE_DELETED = "quote.deleted" as const;
export const TASK_DELETED = "task.deleted" as const;
export const APPOINTMENT_CREATED = "appointment.created" as const;
export const APPOINTMENT_DELETED = "appointment.deleted" as const;

import { CheckSquare, CheckCircle2, Search } from "lucide-react";
import {
  ListContextProvider,
  ResourceContextProvider,
  useGetIdentity,
  useGetList,
  useList,
} from "ra-core";
import { useState, useMemo, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";

import { AddTask } from "./AddTask";
import { TasksIterator } from "./TasksIterator";
import {
  crmAddDays,
  crmEndOfDay,
  crmEndOfWeek,
  crmStartOfDay,
} from "../misc/timezone";
import type { Task } from "../types";

const startOfTodayDate = crmStartOfDay();
const endOfTodayDate = crmEndOfDay();
const endOfTomorrowDate = crmEndOfDay(crmAddDays(new Date(), 1));
const endOfWeekDate = crmEndOfWeek();

const startOfTodayDateISO = (startOfTodayDate ?? new Date()).toISOString();
const endOfTodayDateISO = (endOfTodayDate ?? new Date()).toISOString();
const endOfTomorrowDateISO = (endOfTomorrowDate ?? new Date()).toISOString();
const endOfWeekDateISO = (endOfWeekDate ?? new Date()).toISOString();

const taskFilters = {
  overdue: {
    "due_date@lt": startOfTodayDateISO,
    "done_date@is": null,
  },
  today: {
    "due_date@gte": startOfTodayDateISO,
    "due_date@lte": endOfTodayDateISO,
    "done_date@is": null,
  },
  tomorrow: {
    "due_date@gt": endOfTodayDateISO,
    "due_date@lte": endOfTomorrowDateISO,
    "done_date@is": null,
  },
  thisWeek: {
    "due_date@gt": endOfTomorrowDateISO,
    "due_date@lte": endOfWeekDateISO,
    "done_date@is": null,
  },
  later: {
    "due_date@gt": endOfWeekDateISO,
    "done_date@is": null,
  },
  completed: {
    "done_date@not.is": null,
  },
};

type TaskColumnProps = {
  title: string;
  filter: any;
  showMyTasksOnly: boolean;
  currentUserId?: number;
  sortBy?: "due_date" | "done_date";
  sortOrder?: "ASC" | "DESC";
  searchQuery?: string;
};

const TaskColumn = ({
  title,
  filter,
  showMyTasksOnly,
  currentUserId,
  sortBy = "due_date",
  sortOrder = "ASC",
  searchQuery,
}: TaskColumnProps) => {
  const baseFilter = useMemo(() => {
    let finalFilter = { ...filter };

    if (showMyTasksOnly && currentUserId) {
      // Filter by tagged_user_ids using @cs (contains) operator
      finalFilter["tagged_user_ids@cs"] = `{${currentUserId}}`;
    }

    // Add search filter if provided
    if (searchQuery && searchQuery.trim()) {
      finalFilter["text@ilike"] = `%${searchQuery.trim()}%`;
    }

    return finalFilter;
  }, [filter, showMyTasksOnly, currentUserId, searchQuery]);

  const {
    data: tasks,
    total,
    isPending,
  } = useGetList<Task>(
    "tasks",
    {
      pagination: { page: 1, perPage: 1000 },
      sort: { field: sortBy, order: sortOrder },
      filter: baseFilter,
    },
    { enabled: !showMyTasksOnly || !!currentUserId },
  );

  // Sort tasks based on column type
  const sortedTasks = useMemo(() => {
    if (!tasks) return [];

    // For completed column, sort by done_date descending (most recent first)
    if (sortBy === "done_date") {
      return tasks.sort((a, b) => {
        const dateA = new Date(a.done_date || 0).getTime();
        const dateB = new Date(b.done_date || 0).getTime();
        return dateB - dateA; // Descending order
      });
    }

    // For other columns: only show incomplete tasks, sorted by due_date
    return tasks
      .filter(task => !task.done_date)
      .sort((a, b) => {
        const dateA = new Date(a.due_date || 0).getTime();
        const dateB = new Date(b.due_date || 0).getTime();
        return dateA - dateB;
      });
  }, [tasks, sortBy]);

  const listContext = useList({
    data: sortedTasks,
    isPending,
    resource: "tasks",
    perPage: 1000,
  });

  return (
    <div className="flex flex-col h-full">
      <Card className="flex flex-col h-full border-border/50 shadow-sm">
        <CardHeader className="pb-2.5 px-4 pt-3 border-b border-border/50">
          <CardTitle className="text-xs font-semibold text-muted-foreground uppercase tracking-wider flex items-center gap-2">
            <span>{title}</span>
            {total !== undefined && total > 0 && (
              <span className="text-xs font-medium text-foreground bg-muted px-1.5 py-0.5 rounded">
                {total}
              </span>
            )}
          </CardTitle>
        </CardHeader>
        <CardContent className="flex-1 overflow-y-auto min-h-0 p-2 space-y-2">
          <ResourceContextProvider value="tasks">
            <ListContextProvider value={listContext}>
              {isPending ? (
                <div className="flex items-center justify-center py-8">
                  <p className="text-sm text-muted-foreground">Loading...</p>
                </div>
              ) : (
                <TasksIterator showContact showAll showTimestamp={false} />
              )}
            </ListContextProvider>
          </ResourceContextProvider>
        </CardContent>
      </Card>
    </div>
  );
};

const CompletedTasksDialog = ({
  open,
  onClose,
  showMyTasksOnly,
  currentUserId,
  searchQuery: initialSearchQuery,
}: {
  open: boolean;
  onClose: () => void;
  showMyTasksOnly: boolean;
  currentUserId?: number;
  searchQuery?: string;
}) => {
  const [searchQuery, setSearchQuery] = useState(initialSearchQuery || "");

  // Sync search query when dialog opens or parent search changes
  useEffect(() => {
    if (open && initialSearchQuery !== undefined) {
      setSearchQuery(initialSearchQuery);
    }
  }, [open, initialSearchQuery]);

  const baseFilter = useMemo(() => {
    let finalFilter = { ...taskFilters.completed };

    if (showMyTasksOnly && currentUserId) {
      finalFilter["tagged_user_ids@cs"] = `{${currentUserId}}`;
    }

    if (searchQuery && searchQuery.trim()) {
      finalFilter["text@ilike"] = `%${searchQuery.trim()}%`;
    }

    return finalFilter;
  }, [showMyTasksOnly, currentUserId, searchQuery]);

  const {
    data: tasks,
    total,
    isPending,
  } = useGetList<Task>(
    "tasks",
    {
      pagination: { page: 1, perPage: 1000 },
      sort: { field: "done_date", order: "DESC" },
      filter: baseFilter,
    },
    { enabled: open },
  );

  const sortedTasks = useMemo(() => {
    if (!tasks) return [];
    return tasks.sort((a, b) => {
      const dateA = new Date(a.done_date || 0).getTime();
      const dateB = new Date(b.done_date || 0).getTime();
      return dateB - dateA;
    });
  }, [tasks]);

  const listContext = useList({
    data: sortedTasks,
    isPending,
    resource: "tasks",
    perPage: 1000,
  });

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="lg:max-w-4xl max-h-[90vh] overflow-hidden flex flex-col">
        <DialogHeader>
          <DialogTitle>Completed Tasks</DialogTitle>
          <DialogDescription className="sr-only">
            View all completed tasks
          </DialogDescription>
        </DialogHeader>
        {/* Search Bar */}
        <div className="flex-shrink-0 pb-4">
          <div className="flex flex-grow relative">
            <input
              type="text"
              placeholder="Search completed tasks..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background file:border-0 file:bg-transparent file:text-sm file:font-medium placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50 pr-8"
            />
            <Search className="absolute right-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground pointer-events-none" />
          </div>
        </div>
        <div className="flex-1 overflow-y-auto min-h-0">
          <ResourceContextProvider value="tasks">
            <ListContextProvider value={listContext}>
              {isPending ? (
                <div className="flex items-center justify-center py-8">
                  <p className="text-sm text-muted-foreground">Loading...</p>
                </div>
              ) : sortedTasks && sortedTasks.length > 0 ? (
                <TasksIterator showContact showAll showTimestamp={true} />
              ) : (
                <div className="flex flex-col items-center justify-center py-12 text-center">
                  <CheckCircle2 className="w-12 h-12 text-muted-foreground mb-4 opacity-50" />
                  <p className="text-sm text-muted-foreground">No completed tasks</p>
                </div>
              )}
            </ListContextProvider>
          </ResourceContextProvider>
