        ? date.toISOString()
        : contact.last_seen;
    return {
      id,
      contact_id: contact.id,
      text: lorem.paragraphs(datatype.number({ min: 1, max: 4 })),
      date: date.toISOString(),
      sales_id: contact.sales_id,
      status: random.arrayElement(defaultNoteStatuses).value,
    };
  });
};

