  // Non admins can't access the sales resource
  if (params.resource === "sales") {
    return false;
  }

  return true;
};

import type { DataProvider, Identifier } from "ra-core";

import {
  COMPANY_CREATED,
  CONTACT_CREATED,
  CONTACT_NOTE_CREATED,
  DEAL_CREATED,
  DEAL_NOTE_CREATED,
  QUOTE_CREATED,
  QUOTE_UPDATED,
  TASK_CREATED,
  TASK_COMPLETED,
  DEAL_STATUS_CHANGED,
  DEAL_ARCHIVED,
  DEAL_UNARCHIVED,
  NOTE_DELETED,
  QUOTE_DELETED,
  TASK_DELETED,
  APPOINTMENT_CREATED,
  APPOINTMENT_DELETED,
} from "../../consts";
import type {
  Activity,
  Company,
