import { cn } from "@/lib/utils";
import { Card, CardContent } from "@/components/ui/card";

import { NoteInputs } from "./NoteInputs";

const foreignKeyMapping = {
  contacts: "contact_id",
  "lead-journey": "deal_id",
};

