const NoteCreateButton = ({
  reference,
  record,
}: {
  reference: "contacts" | "lead-journey";
  record: RaRecord<Identifier>;
}) => {
  const [update] = useUpdate();
  const notify = useNotify();
  const { identity } = useGetIdentity();
  const { reset } = useFormContext();
  const { refetch } = useListContext();

  if (!record) return null;

  const resetValues: {
    text: null;
    attachments: null;
    tagged_user_ids?: undefined;
  } = {
    text: null,
    attachments: null,
    tagged_user_ids: undefined,
  };

  const handleSuccess = (data: any) => {
    reset(resetValues, { keepValues: false });
    refetch();
    update(reference, {
      id: (record && record.id) as unknown as Identifier,
      data: { last_seen: new Date().toISOString() },
      previousData: record,
    });
    notify("Note added");
  };

  return (
    <div className="flex justify-end">
      <SaveButton
        type="button"
        label="Add this note"
        transform={(data) => {
          const { tagged_user_ids, ...restData } = data;
          const result: any = {
            ...restData,
            [foreignKeyMapping[reference]]: record.id,
            sales_id: identity?.id,
            // Always save notes with current time
            date: new Date().toISOString(),
          };
          // Only include tagged_user_ids if it's a non-empty array
          // This avoids schema cache issues if the column doesn't exist yet
          if (Array.isArray(tagged_user_ids) && tagged_user_ids.length > 0) {
            result.tagged_user_ids = tagged_user_ids;
          }
          return result;
        }}
        mutationOptions={{
          onSuccess: handleSuccess,
        }}
      >
        Add this note
      </SaveButton>
    </div>
  );
};

import { Paperclip } from "lucide-react";

import type { AttachmentNote, ContactNote, DealNote } from "../types";

export const NoteAttachments = ({ note }: { note: ContactNote | DealNote }) => {
  if (!note.attachments || note.attachments.length === 0) {
    return null;
  }

  const imageAttachments = note.attachments.filter(
    (attachment: AttachmentNote) => isImageMimeType(attachment.type),
  );
  const otherAttachments = note.attachments.filter(
    (attachment: AttachmentNote) => !isImageMimeType(attachment.type),
  );

  return (
    <div className="flex flex-col">
      {imageAttachments.length > 0 && (
        <div className="grid grid-cols-4 gap-8">
          {imageAttachments.map((attachment: AttachmentNote, index: number) => (
            <div key={index}>
              <img
                src={attachment.src}
                alt={attachment.title}
                className="w-[200px] h-[100px] object-cover cursor-pointer object-left border border-border"
                onClick={() => window.open(attachment.src, "_blank")}
              />
            </div>
          ))}
        </div>
      )}
      {otherAttachments.length > 0 &&
        otherAttachments.map((attachment: AttachmentNote, index: number) => (
          <div key={index} className="flex items-center gap-2">
            <Paperclip className="w-4 h-4" />
            <a
              href={attachment.src}
              target="_blank"
              rel="noopener noreferrer"
              className="underline hover:no-underline"
            >
              {attachment.title}
            </a>
          </div>
        ))}
    </div>
  );
};

const isImageMimeType = (mimeType?: string): boolean => {
  if (!mimeType) {
    return false;
  }
  return mimeType.startsWith("image/");
};

import { CircleX, Edit, Save, Trash2 } from "lucide-react";
import {
  Form,
  useDelete,
  useNotify,
  useResourceContext,
  useUpdate,
  WithRecord,
} from "ra-core";
import { useState, type ReactNode } from "react";
import type { FieldValues, SubmitHandler } from "react-hook-form";
import { ReferenceField } from "@/components/admin/reference-field";
import { Button } from "@/components/ui/button";
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { Card, CardContent, CardHeader } from "@/components/ui/card";

import { RelativeDate } from "../misc/RelativeDate";
import { Status } from "../misc/Status";
import { SaleName } from "../sales/SaleName";
import type { ContactNote, DealNote } from "../types";
import { NoteInputs } from "./NoteInputs";

export const Note = ({
  showStatus,
  note,
  context,
}: {
  showStatus?: boolean;
  note: DealNote | ContactNote;
  isLast: boolean;
  context?: ReactNode;
}) => {
  const [isHover, setHover] = useState(false);
  const [isEditing, setEditing] = useState(false);
  const resource = useResourceContext();
  const notify = useNotify();

  const [update, { isPending }] = useUpdate();

  const [deleteNote] = useDelete(
    resource,
    { id: note.id, previousData: note },
    {
      mutationMode: "undoable",
      onSuccess: () => {
        notify("Note deleted", { type: "info", undoable: true });
      },
    },
  );

  const handleDelete = () => {
    deleteNote();
  };

  const handleEnterEditMode = () => {
    setEditing(!isEditing);
  };

  const handleCancelEdit = () => {
    setEditing(false);
    setHover(false);
  };

  const handleNoteUpdate: SubmitHandler<FieldValues> = (values) => {
    update(
      resource,
      { id: note.id, data: values, previousData: note },
      {
        onSuccess: () => {
          setEditing(false);
          setHover(false);
        },
      },
    );
  };

  return (
    <Card
      id={`note-${note.id}`}
      className="hover:shadow-md transition-shadow py-3 gap-2"
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
    >
      <CardHeader className="pb-2 px-4 pt-0">
        <div className="flex items-start justify-between gap-3">
          <div className="flex items-start gap-2 flex-1 min-w-0">
            <div className="flex-1 min-w-0">
              {/* Keep header on a single line; truncate if it overflows */}
              <div className="flex items-center gap-2 min-w-0 whitespace-nowrap overflow-hidden">
                <span className="text-sm font-medium flex-shrink-0">
                  <ReferenceField
                    record={note}
                    resource={resource}
                    source="sales_id"
                    reference="sales"
                    link={false}
                  >
                    <WithRecord render={(record) => <SaleName sale={record} />} />
                  </ReferenceField>
                </span>
                <span className="text-sm text-muted-foreground truncate min-w-0 overflow-hidden text-ellipsis">
                  added a note
                  {context ? (
                    <>
                      {" "}
                      on <span className="inline whitespace-nowrap">{context}</span>
                    </>
                  ) : null}
                </span>
                {showStatus && note.status && (
                  <Status className="ml-1 flex-shrink-0" status={note.status} />
                )}
              </div>
            </div>
          </div>
          <div className={`flex items-center gap-1 ${isHover ? "opacity-100" : "opacity-0"} transition-opacity`}>
            <TooltipProvider>
              <Tooltip>
                <TooltipTrigger asChild>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={handleEnterEditMode}
                    className="h-7 w-7 p-0 cursor-pointer"
                  >
                    <Edit className="w-3.5 h-3.5" />
                  </Button>
                </TooltipTrigger>
                <TooltipContent>
                  <p>Edit note</p>
                </TooltipContent>
              </Tooltip>
            </TooltipProvider>
            <TooltipProvider>
              <Tooltip>
                <TooltipTrigger asChild>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={handleDelete}
                    className="h-7 w-7 p-0 cursor-pointer text-destructive hover:text-destructive"
                  >
                    <Trash2 className="w-3.5 h-3.5" />
                  </Button>
                </TooltipTrigger>
                <TooltipContent>
                  <p>Delete note</p>
                </TooltipContent>
              </Tooltip>
            </TooltipProvider>
          </div>
        </div>
      </CardHeader>
      <CardContent className="pt-0 px-4 pb-0">
        {isEditing ? (
          <Form onSubmit={handleNoteUpdate} record={note}>
            <NoteInputs showStatus={showStatus} />
            <div className="flex justify-end gap-2 mt-3">
              <Button
                variant="outline"
                onClick={handleCancelEdit}
                type="button"
                size="sm"
                className="cursor-pointer"
              >
                <CircleX className="w-4 h-4 mr-2" />
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={isPending}
                size="sm"
                className="cursor-pointer"
              >
                <Save className="w-4 h-4 mr-2" />
                Save
              </Button>
            </div>
          </Form>
        ) : (
          <div className="flex items-start justify-between gap-3">
            <div className="flex-1 [&_p:empty]:min-h-[0.75em]">
              {note.text?.split("\n").map((paragraph: string, index: number) => (
                <p className="text-sm leading-5 m-0 mb-1.5 last:mb-0" key={index}>
                  {paragraph}
                </p>
              ))}
            </div>
            <span className="text-xs text-muted-foreground whitespace-nowrap mt-0.5">
              <RelativeDate date={note.date} />
            </span>
          </div>
        )}
      </CardContent>
    </Card>
  );
