                </TabsTrigger>
                <TabsTrigger value="tasks" className="relative gap-2">
                  <span>Tasks</span>
                  {newTasksCount > 0 && (
                    <span className="h-5 min-w-5 px-1.5 bg-primary text-primary-foreground rounded-full flex items-center justify-center text-[10px] font-semibold">
                      {newTasksCount}
