export const ContactMergeButton = () => {
  const [mergeDialogOpen, setMergeDialogOpen] = useState(false);
  return (
    <>
      <Button
        variant="outline"
        className="h-6 cursor-pointer"
        size="sm"
        onClick={() => setMergeDialogOpen(true)}
      >
        <Merge className="w-4 h-4" />
        Merge with another contact
      </Button>
      <ContactMergeDialog
        open={mergeDialogOpen}
        onClose={() => setMergeDialogOpen(false)}
      />
    </>
  );
};

interface ContactMergeDialogProps {
  open: boolean;
  onClose: () => void;
}

const ContactMergeDialog = ({ open, onClose }: ContactMergeDialogProps) => {
  const loserContact = useRecordContext<Contact>();
  const notify = useNotify();
  const redirect = useRedirect();
  const dataProvider = useDataProvider();
  const [winnerId, setWinnerId] = useState<Identifier | null>(null);
  const [suggestedWinnerId, setSuggestedWinnerId] = useState<Identifier | null>(
    null,
  );
  const [isMerging, setIsMerging] = useState(false);
  const { mutateAsync } = useMutation({
    mutationKey: ["contacts", "merge", { loserId: loserContact?.id, winnerId }],
    mutationFn: async () => {
      return dataProvider.mergeContacts(loserContact?.id, winnerId);
    },
  });

  // Find potential contacts with matching first and last name
  const { data: matchingContacts } = useGetList(
    "contacts",
    {
      filter: {
        first_name: loserContact?.first_name,
        last_name: loserContact?.last_name,
        "id@neq": `${loserContact?.id}`, // Exclude current contact
      },
      pagination: { page: 1, perPage: 10 },
    },
    { enabled: open && !!loserContact },
  );

  // Get counts of items to be merged
  const canFetchCounts = open && !!loserContact && !!winnerId;
  const { total: tasksCount } = useGetManyReference(
    "tasks",
    {
      target: "contact_id",
      id: loserContact?.id,
      pagination: { page: 1, perPage: 1 },
    },
    { enabled: canFetchCounts },
  );

  const { total: notesCount } = useGetManyReference(
    "contactNotes",
    {
      target: "contact_id",
      id: loserContact?.id,
      pagination: { page: 1, perPage: 1 },
    },
    { enabled: canFetchCounts },
  );

  const { total: dealsCount } = useGetList(
    "deals",
    {
      filter: { "contact_ids@cs": `{${loserContact?.id}}` },
      pagination: { page: 1, perPage: 1 },
    },
    { enabled: canFetchCounts },
  );

  useEffect(() => {
    if (matchingContacts && matchingContacts.length > 0) {
      const suggestedWinnerId = matchingContacts[0].id;
      setSuggestedWinnerId(suggestedWinnerId);
      setWinnerId(suggestedWinnerId);
    }
  }, [matchingContacts]);

  const handleMerge = async () => {
    if (!winnerId || !loserContact) {
      notify("Please select a contact to merge with", { type: "warning" });
      return;
    }

    try {
      setIsMerging(true);
      await mutateAsync();
      setIsMerging(false);
      notify("Contacts merged successfully", { type: "success" });
      redirect(`/contacts/${winnerId}/show`);
      onClose();
    } catch (error) {
      setIsMerging(false);
      notify("Failed to merge contacts", { type: "error" });
      console.error("Merge failed:", error);
    }
  };

  if (!loserContact) return null;

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="md:min-w-lg max-w-2xl">
        <DialogHeader>
          <DialogTitle>Merge Contact</DialogTitle>
          <DialogDescription>
            Merge this contact with another one.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          <div className="p-4 bg-primary/5 rounded-lg border border-primary/20">
            <p className="font-medium text-sm">
              Current Contact (will be deleted)
            </p>
            <div className="font-medium text-sm mt-4">{contactOptionText}</div>

            <div className="flex justify-center my-4">
              <ArrowDown className="h-5 w-5 text-muted-foreground" />
            </div>

            <p className="font-medium text-sm mb-2">
              Target Contact (will be kept)
            </p>
            <Form>
              <ReferenceInput
                source="winner_id"
                reference="contacts"
                filter={{ "id@neq": loserContact.id }}
              >
                <AutocompleteInput
                  label=""
                  optionText={contactOptionText}
                  validate={required()}
                  onChange={setWinnerId}
                  defaultValue={suggestedWinnerId}
                  helperText={false}
                />
              </ReferenceInput>
            </Form>
          </div>

          {winnerId && (
            <>
              <div className="space-y-2">
                <p className="font-medium text-sm">What will be merged:</p>
                <ul className="text-sm text-muted-foreground space-y-1 ml-4">
                  {notesCount != null && notesCount > 0 && (
                    <li>
                      â€¢ {notesCount} note
                      {notesCount !== 1 ? "s" : ""} will be reassigned
                    </li>
                  )}
                  {tasksCount != null && tasksCount > 0 && (
                    <li>
                      â€¢ {tasksCount} task
                      {tasksCount !== 1 ? "s" : ""} will be reassigned
                    </li>
                  )}
                  {dealsCount != null && dealsCount > 0 && (
                    <li>
                      â€¢ {dealsCount} deal
                      {dealsCount !== 1 ? "s" : ""} will be updated
                    </li>
                  )}
                  {loserContact.email_jsonb?.length > 0 && (
                    <li>
                      â€¢ {loserContact.email_jsonb.length} email address
                      {loserContact.email_jsonb.length !== 1 ? "es" : ""} will
                      be added
                    </li>
                  )}
                  {loserContact.phone_jsonb?.length > 0 && (
                    <li>
                      â€¢ {loserContact.phone_jsonb.length} phone number
                      {loserContact.phone_jsonb.length !== 1 ? "s" : ""} will be
                      added
                    </li>
                  )}
                  {!notesCount &&
                    !tasksCount &&
                    !dealsCount &&
                    !loserContact.email_jsonb?.length &&
                    !loserContact.phone_jsonb?.length && (
                      <li className="text-muted-foreground/60">
                        No additional data to merge
                      </li>
                    )}
                </ul>
              </div>
              <Alert variant="destructive">
                <AlertTriangle className="h-4 w-4" />
                <AlertTitle>Warning: Destructive Operation</AlertTitle>
                <AlertDescription>
                  All data will be transferred to the second contact. This
                  action cannot be undone.
                </AlertDescription>
              </Alert>
            </>
          )}
        </div>

        <DialogFooter>
          <Button variant="ghost" onClick={onClose} disabled={isMerging}>
            <CircleX />
            Cancel
          </Button>
          <Button onClick={handleMerge} disabled={!winnerId || isMerging}>
            <Merge />
            {isMerging ? "Merging..." : "Merge Contacts"}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
};

import { subMonths } from "date-fns";
import isEqual from "lodash/isEqual";
import {
  CheckSquare,
  Clock,
  Stethoscope,
  Tag,
  TrendingUp,
  Users,
} from "lucide-react";
import {
  FilterLiveForm,
  useGetIdentity,
  useGetList,
  useListContext,
} from "ra-core";

import { SearchInput } from "@/components/admin/search-input";
import { Badge } from "@/components/ui/badge";

import { FilterCategory } from "../filters/FilterCategory";
import { FilterOptionButton } from "../filters/FilterOptionButton";
import {
  buildInFilter,
  buildOverlapFilter,
  parseInFilter,
  parseOverlapFilter,
  RangeFilter,
  toggleValueInList,
} from "../filters/filterUtils";
import { useConfigurationContext } from "../root/ConfigurationContext";
import {
  crmEndOfYesterday,
  crmStartOfMonth,
  crmStartOfWeek,
} from "../misc/timezone";

export const ContactListFilter = () => {
  const { filterValues = {}, setFilters } = useListContext();
  const { leadStages } = useConfigurationContext();
  const { identity } = useGetIdentity();
  const { data } = useGetList("tags", {
    pagination: { page: 1, perPage: 10 },
    sort: { field: "name", order: "ASC" },
  });
  const { data: servicesData } = useGetList("services", {
    pagination: { page: 1, perPage: 50 },
    sort: { field: "name", order: "ASC" },
  });
  const { data: salesData } = useGetList("sales", {
    pagination: { page: 1, perPage: 50 },
    sort: { field: "first_name", order: "ASC" },
  });

  const startOfWeek = crmStartOfWeek();
  const startOfMonth = crmStartOfMonth();
  const endOfYesterday = crmEndOfYesterday();
  const lastSeenRanges = Array.isArray(filterValues.last_seen_ranges)
    ? (filterValues.last_seen_ranges as RangeFilter[])
    : [];
  const selectedLeadStages = parseInFilter(filterValues["lead_stage@in"]);
  const selectedServices = parseOverlapFilter(
    filterValues["services_interested@ov"],
  );
  const selectedTags = parseOverlapFilter(filterValues["tags@ov"]);
  const selectedManagers = parseInFilter(filterValues["sales_id@in"]);
  const hasPendingTasks =
    typeof filterValues["nb_tasks@gt"] !== "undefined" &&
    filterValues["nb_tasks@gt"] !== null;

  const updateFilters = (changes: Record<string, unknown>) => {
    const next = { ...filterValues, ...changes };
    Object.entries(changes).forEach(([key, value]) => {
      if (
        value === undefined ||
        value === null ||
        (Array.isArray(value) && value.length === 0)
      ) {
        delete next[key];
      }
    });
    setFilters(next);
  };

  const toggleLastSeenRange = (range: RangeFilter) => {
    const isSelected = lastSeenRanges.some((item) => isEqual(item, range));
    const nextRanges = isSelected
      ? lastSeenRanges.filter((item) => !isEqual(item, range))
      : [...lastSeenRanges, range];
    updateFilters({
      last_seen_ranges: nextRanges.length ? nextRanges : undefined,
    });
  };

  const toggleLeadStage = (value: string) => {
    const next = toggleValueInList(selectedLeadStages, value);
    updateFilters({ "lead_stage@in": buildInFilter(next) });
  };

  const toggleService = (id: number) => {
    const next = toggleValueInList(selectedServices, id);
    updateFilters({ "services_interested@ov": buildOverlapFilter(next) });
  };

  const toggleTag = (id: number) => {
    const next = toggleValueInList(selectedTags, id);
    updateFilters({ "tags@ov": buildOverlapFilter(next) });
  };

  const toggleManager = (id: string | number | undefined) => {
    if (typeof id === "undefined") return;
    const next = toggleValueInList(selectedManagers, id);
    updateFilters({ "sales_id@in": buildInFilter(next) });
  };

  const toggleTasks = () => {
    if (hasPendingTasks) {
      updateFilters({ "nb_tasks@gt": undefined });
    } else {
      updateFilters({ "nb_tasks@gt": 0 });
    }
  };

  const clearLastSeen = () => updateFilters({ last_seen_ranges: undefined });
  const clearStatuses = () => updateFilters({ "lead_stage@in": undefined });
  const clearServices = () =>
    updateFilters({ "services_interested@ov": undefined });
  const clearTags = () => updateFilters({ "tags@ov": undefined });
  const clearManagers = () => updateFilters({ "sales_id@in": undefined });

  return (
    <div className="w-52 min-w-52 order-first pt-0.75 flex flex-col gap-4">
      <FilterLiveForm>
        <SearchInput source="q" placeholder="Search name, company..." />
      </FilterLiveForm>

      <FilterCategory
        label="Last activity"
        icon={<Clock />}
        count={lastSeenRanges.length}
        onClear={clearLastSeen}
      >
        <FilterOptionButton
          label="Today"
          selected={lastSeenRanges.some((range) =>
            isEqual(range, {
              field: "last_seen",
              gte: endOfYesterday?.toISOString(),
            }),
          )}
          onToggle={() =>
            toggleLastSeenRange({
              field: "last_seen",
              gte: endOfYesterday?.toISOString(),
            })
          }
        />
        <FilterOptionButton
          label="This week"
          selected={lastSeenRanges.some((range) =>
            isEqual(range, {
              field: "last_seen",
              gte: startOfWeek?.toISOString(),
            }),
          )}
          onToggle={() =>
            toggleLastSeenRange({
              field: "last_seen",
              gte: startOfWeek?.toISOString(),
            })
          }
        />
        <FilterOptionButton
          label="Before this week"
          selected={lastSeenRanges.some((range) =>
            isEqual(range, {
              field: "last_seen",
              lte: startOfWeek?.toISOString(),
            }),
          )}
          onToggle={() =>
            toggleLastSeenRange({
              field: "last_seen",
              lte: startOfWeek?.toISOString(),
            })
          }
        />
        <FilterOptionButton
          label="Before this month"
          selected={lastSeenRanges.some((range) =>
            isEqual(range, {
              field: "last_seen",
              lte: startOfMonth?.toISOString(),
            }),
          )}
          onToggle={() =>
            toggleLastSeenRange({
              field: "last_seen",
              lte: startOfMonth?.toISOString(),
            })
          }
        />
        <FilterOptionButton
          label="Before last month"
          selected={lastSeenRanges.some((range) =>
            isEqual(range, {
              field: "last_seen",
              lte: subMonths(startOfMonth ?? new Date(), 1).toISOString(),
            }),
          )}
          onToggle={() =>
            toggleLastSeenRange({
              field: "last_seen",
              lte: subMonths(startOfMonth ?? new Date(), 1).toISOString(),
            })
          }
        />
      </FilterCategory>

      <FilterCategory
        label="Status"
        icon={<TrendingUp />}
        count={selectedLeadStages.length}
        onClear={clearStatuses}
      >
        {leadStages.map((stage) => (
          <FilterOptionButton
            key={stage.value}
            label={stage.label}
            selected={selectedLeadStages.includes(stage.value)}
            onToggle={() => toggleLeadStage(stage.value)}
          />
        ))}
      </FilterCategory>

      <FilterCategory
        label="Services interested in"
        icon={<Stethoscope />}
        count={selectedServices.length}
        onClear={clearServices}
      >
        {servicesData && servicesData.length > 0 ? (
          servicesData.map((service) => (
            <FilterOptionButton
              key={service.id}
              label={service.name}
              selected={selectedServices.includes(String(service.id))}
              onToggle={() => toggleService(service.id)}
            />
          ))
        ) : (
          <div className="text-xs text-muted-foreground">
            No services available
          </div>
        )}
      </FilterCategory>

      <FilterCategory
        label="Tags"
        icon={<Tag />}
        count={selectedTags.length}
        onClear={clearTags}
      >
        {data &&
          data.map((record) => (
            <FilterOptionButton
              key={record.id}
              label={
                <Badge
                  variant="secondary"
                  className="text-black text-xs font-normal cursor-pointer"
                  style={{
                    backgroundColor: record?.color,
                  }}
                >
                  {record?.name}
                </Badge>
              }
              selected={selectedTags.includes(String(record.id))}
              onToggle={() => toggleTag(record.id)}
            />
          ))}
      </FilterCategory>

      <FilterCategory
        icon={<CheckSquare />}
        label="Tasks"
        count={hasPendingTasks ? 1 : 0}
        onClear={() => updateFilters({ "nb_tasks@gt": undefined })}
      >
        <FilterOptionButton
          label={"With pending tasks"}
          selected={hasPendingTasks}
          onToggle={toggleTasks}
        />
      </FilterCategory>

      <FilterCategory
        icon={<Users />}
        label="Account Manager"
        count={selectedManagers.length}
        onClear={clearManagers}
      >
        <FilterOptionButton
          label={"Me"}
          selected={selectedManagers.includes(String(identity?.id))}
          onToggle={() => toggleManager(identity?.id)}
        />
        {salesData &&
          salesData
            .filter((manager) => manager.id !== identity?.id)
            .map((manager) => (
              <FilterOptionButton
                key={manager.id}
                label={`${manager.first_name} ${manager.last_name}`}
                selected={selectedManagers.includes(String(manager.id))}
                onToggle={() => toggleManager(manager.id)}
              />
            ))}
      </FilterCategory>
    </div>
  );
};

import { RecordContextProvider, useListContext, useGetList } from "ra-core";
import { type MouseEvent, useCallback, useMemo, useRef } from "react";
import { Link } from "react-router";
import { ReferenceField } from "@/components/admin/reference-field";
import { TextField } from "@/components/admin/text-field";
import { Checkbox } from "@/components/ui/checkbox";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { useIsMobile } from "@/hooks/use-mobile";

import { Status } from "../misc/Status";
import type { Contact, Deal } from "../types";
import { TagsList } from "./TagsList";
import { useConfigurationContext } from "../root/ConfigurationContext";
import { formatRelativeOrDate } from "../misc/timezone";

// Helper function to get badge color for lead stage
const getStageBadgeColor = (stage: string): string => {
  switch (stage) {
    case "new":
      return "bg-blue-500 hover:bg-blue-600";
    case "contacted":
      return "bg-yellow-500 hover:bg-yellow-600";
    case "quoted":
      return "bg-purple-500 hover:bg-purple-600";
    case "qualified":
      return "bg-green-500 hover:bg-green-600";
    case "not-qualified":
      return "bg-red-500 hover:bg-red-600";
    case "converted":
      return "bg-emerald-600 hover:bg-emerald-700";
    default:
      return "bg-gray-500 hover:bg-gray-600";
  }
};

export const ContactListContent = () => {
  const {
    data: contacts,
    error,
    isPending,
    onSelect,
    onToggleItem,
    selectedIds,
  } = useListContext<Contact>();
  const isSmall = useIsMobile();
  const { leadStages } = useConfigurationContext();
  const lastClickedIndexRef = useRef<number | null>(null);

  // Fetch all deals to get contact stages and identify clients
  const { data: allDeals } = useGetList<Deal>("lead-journey", {
    pagination: { page: 1, perPage: 10000 },
    sort: { field: "id", order: "ASC" },
    filter: { "archived_at@is": null, "lead_id@not.is": null },
  }, { enabled: !isPending });

  // Create a map of contact ID to deal stage
  const contactStageMap = useMemo(() => {
    if (!allDeals) return new Map<number | string, string>();
    const map = new Map<number | string, string>();
    allDeals.forEach((deal) => {
      if (deal.lead_id) {
        map.set(deal.lead_id, deal.stage);
      }
    });
    return map;
  }, [allDeals]);

  // Create a Set of client contact IDs for quick lookup
  const clientContactIds = useMemo(() => {
    if (!allDeals) return new Set<number | string>();
    return new Set(
      allDeals
        .filter((deal) => deal.stage === "converted")
        .map((deal) => deal.lead_id)
        .filter((id): id is number | string => id !== undefined && id !== null)
    );
  }, [allDeals]);

  // StopPropagation does not work for some reason on Checkbox, this handler is a workaround
  const handleLinkClick = useCallback(function handleLinkClick(
    e: MouseEvent<HTMLAnchorElement>,
  ) {
    // Prevent navigation if clicking on checkbox or if shift key is pressed
    if (
      e.target instanceof HTMLButtonElement ||
      (e.target as HTMLElement).closest('[data-slot="checkbox"]') ||
      e.shiftKey
    ) {
      e.preventDefault();
    }
  }, []);

  const handleCheckboxClick = useCallback(
    (contactId: number | string, index: number, event: MouseEvent) => {
      event.stopPropagation();
      event.preventDefault();

      if (event.shiftKey && lastClickedIndexRef.current !== null) {
        // Shift-click: select range from last clicked to current
        const start = Math.min(lastClickedIndexRef.current, index);
        const end = Math.max(lastClickedIndexRef.current, index);
        const rangeIds = contacts
          .slice(start, end + 1)
          .map((contact) => contact.id);

        // Click would toggle the current item, so apply the *toggled* state to the whole range.
        // Use onSelect (set selected IDs in one shot) to avoid flaky toggling/batching issues.
        const currentIsSelected = selectedIds.includes(contactId);
        const targetIsSelected = !currentIsSelected;

        if (onSelect) {
          const rangeSet = new Set(rangeIds);
          const selectedSet = new Set(selectedIds);
          if (targetIsSelected) {
            rangeIds.forEach((id) => selectedSet.add(id));
          } else {
            selectedIds.forEach((id) => {
              if (rangeSet.has(id)) selectedSet.delete(id);
            });
          }
          onSelect(Array.from(selectedSet));
        } else {
          // Fallback (should rarely happen): toggle items to match target state
          rangeIds.forEach((id) => {
            const isSelected = selectedIds.includes(id);
            if (isSelected !== targetIsSelected) {
              onToggleItem(id);
            }
          });
        }
      } else {
        // Normal click: toggle single item
        onToggleItem(contactId);
      }
      lastClickedIndexRef.current = index;
    },
    [contacts, onSelect, onToggleItem, selectedIds],
  );

  if (isPending) {
    return <Skeleton className="w-full h-9" />;
  }

  if (error) {
    return null;
  }
  const now = Date.now();

  return (
    <div className="divide-y">
      {contacts.map((contact, index) => (
        <RecordContextProvider key={contact.id} value={contact}>
          <Link
            to={`/contacts/${contact.id}/show`}
            className="flex flex-row gap-4 items-center px-4 py-2 hover:bg-muted transition-colors first:rounded-t-xl last:rounded-b-xl"
            onClick={handleLinkClick}
          >
            <div 
              onClick={(e) => {
                e.stopPropagation();
                e.preventDefault();
              }}
            >
              <Checkbox
                className="cursor-pointer"
                checked={selectedIds.includes(contact.id)}
                // Use mousedown to reliably capture shiftKey with Radix checkbox
                onMouseDown={(e) => handleCheckboxClick(contact.id, index, e)}
                // Prevent Radix default click toggling; selection is controlled by react-admin
                onClick={(e) => {
                  e.preventDefault();
                  e.stopPropagation();
                }}
              />
            </div>
            <div className="flex-1 min-w-0">
              <div className="font-medium flex items-center gap-2">
                {`${contact.first_name ?? ""} ${contact.last_name ?? ""}`.trim() || "New Lead"}
              </div>
              <div className="text-sm text-muted-foreground">
                {contact.title}
                {contact.title && contact.company_id != null && " at "}
                {contact.company_id != null && (
                  <ReferenceField
                    source="company_id"
                    reference="companies"
                    link={false}
                  >
                    <TextField source="name" />
                  </ReferenceField>
                )}
                {contact.nb_tasks
                  ? ` - ${contact.nb_tasks} task${
                      contact.nb_tasks > 1 ? "s" : ""
                    }`
                  : ""}
                &nbsp;&nbsp;
                <TagsList />
              </div>
            </div>
            {contact.last_seen && (
              <div className="text-right ml-4 flex items-center gap-2">
                {/* Lead Stage Badge */}
                {(() => {
                  const stage = contactStageMap.get(contact.id);
                  if (stage) {
                    const stageConfig = leadStages.find((s) => s.value === stage);
                    const label = stage === "converted" ? "Client" : (stageConfig?.label || stage);
                    return (
                      <Badge
                        variant="default"
                        className={`text-xs text-white ${getStageBadgeColor(stage)}`}
                      >
                        {label}
                      </Badge>
                    );
                  }
                  return null;
                })()}
                <div
                  className="text-sm text-muted-foreground"
                  title={contact.last_seen}
                >
                  {!isSmall && "last activity "}
                  {formatRelativeOrDate(contact.last_seen, new Date(now))}{" "}
                  <Status status={contact.status} />
                </div>
              </div>
            )}
          </Link>
        </RecordContextProvider>
      ))}

      {contacts.length === 0 && (
        <div className="p-4">
          <div className="text-muted-foreground">No contacts found</div>
        </div>
      )}
    </div>
  );
};

