  toggleValueInList,
} from "../filters/filterUtils";
import { useConfigurationContext } from "../root/ConfigurationContext";
import {
  crmEndOfYesterday,
  crmStartOfMonth,
  crmStartOfWeek,
} from "../misc/timezone";

export const ClientListFilter = () => {
  const { filterValues = {}, setFilters } = useListContext();
  const { identity } = useGetIdentity();
  const { data } = useGetList("tags", {
    pagination: { page: 1, perPage: 10 },
    sort: { field: "name", order: "ASC" },
  });
  const { data: servicesData } = useGetList("services", {
    pagination: { page: 1, perPage: 50 },
    sort: { field: "name", order: "ASC" },
  });
  const { data: salesData } = useGetList("sales", {
    pagination: { page: 1, perPage: 50 },
    sort: { field: "first_name", order: "ASC" },
  });
  const startOfWeek = crmStartOfWeek();
  const startOfMonth = crmStartOfMonth();
  const endOfYesterday = crmEndOfYesterday();
  const lastSeenRanges = Array.isArray(filterValues.last_seen_ranges)
    ? (filterValues.last_seen_ranges as RangeFilter[])
    : [];
  const selectedServices = parseOverlapFilter(
    filterValues["services_interested@ov"],
  );
  const selectedTags = parseOverlapFilter(filterValues["tags@ov"]);
  const selectedManagers = parseInFilter(filterValues["sales_id@in"]);
  const hasPendingTasks =
    typeof filterValues["nb_tasks@gt"] !== "undefined" &&
    filterValues["nb_tasks@gt"] !== null;
  const archivedState = filterValues.hasArchivedDeals;

  const updateFilters = (changes: Record<string, unknown>) => {
    const next = { ...filterValues, ...changes };
    Object.entries(changes).forEach(([key, value]) => {
      if (
        value === undefined ||
        value === null ||
        (Array.isArray(value) && value.length === 0)
      ) {
        delete next[key];
      }
    });
    setFilters(next);
  };

  const toggleLastSeenRange = (range: RangeFilter) => {
    const isSelected = lastSeenRanges.some((item) => isEqual(item, range));
    const nextRanges = isSelected
      ? lastSeenRanges.filter((item) => !isEqual(item, range))
      : [...lastSeenRanges, range];
    updateFilters({
      last_seen_ranges: nextRanges.length ? nextRanges : undefined,
    });
  };

  const toggleService = (id: number) => {
    const next = toggleValueInList(selectedServices, id);
    updateFilters({ "services_interested@ov": buildOverlapFilter(next) });
  };

  const toggleTag = (id: number) => {
    const next = toggleValueInList(selectedTags, id);
    updateFilters({ "tags@ov": buildOverlapFilter(next) });
  };

  const toggleManager = (id: string | number | undefined) => {
    if (typeof id === "undefined") return;
    const next = toggleValueInList(selectedManagers, id);
    updateFilters({ "sales_id@in": buildInFilter(next) });
  };

  const toggleTasks = () => {
    if (hasPendingTasks) {
      updateFilters({ "nb_tasks@gt": undefined });
    } else {
      updateFilters({ "nb_tasks@gt": 0 });
    }
  };

  const setArchivedFilter = (value: boolean | undefined) => {
    updateFilters({ hasArchivedDeals: value });
  };

  const clearLastSeen = () => updateFilters({ last_seen_ranges: undefined });
  const clearServices = () =>
    updateFilters({ "services_interested@ov": undefined });
  const clearTags = () => updateFilters({ "tags@ov": undefined });
  const clearManagers = () => updateFilters({ "sales_id@in": undefined });

  return (
    <div className="w-52 min-w-52 order-first pt-0.75 flex flex-col gap-4">
      <FilterLiveForm>
        <SearchInput source="q" placeholder="Search name, company..." />
      </FilterLiveForm>

      <FilterCategory
        label="Last activity"
        icon={<Clock />}
        count={lastSeenRanges.length}
        onClear={clearLastSeen}
      >
        <FilterOptionButton
          label="Today"
          selected={lastSeenRanges.some((range) =>
            isEqual(range, {
              field: "last_seen",
              gte: endOfYesterday?.toISOString(),
            }),
          )}
          onToggle={() =>
            toggleLastSeenRange({
              field: "last_seen",
              gte: endOfYesterday?.toISOString(),
            })
          }
        />
        <FilterOptionButton
          label="This week"
          selected={lastSeenRanges.some((range) =>
            isEqual(range, {
              field: "last_seen",
              gte: startOfWeek?.toISOString(),
            }),
          )}
          onToggle={() =>
            toggleLastSeenRange({
              field: "last_seen",
              gte: startOfWeek?.toISOString(),
            })
          }
        />
        <FilterOptionButton
          label="Before this week"
          selected={lastSeenRanges.some((range) =>
            isEqual(range, {
              field: "last_seen",
              lte: startOfWeek?.toISOString(),
            }),
          )}
          onToggle={() =>
            toggleLastSeenRange({
              field: "last_seen",
              lte: startOfWeek?.toISOString(),
            })
          }
        />
        <FilterOptionButton
          label="Before this month"
          selected={lastSeenRanges.some((range) =>
            isEqual(range, {
              field: "last_seen",
              lte: startOfMonth?.toISOString(),
            }),
          )}
          onToggle={() =>
            toggleLastSeenRange({
              field: "last_seen",
              lte: startOfMonth?.toISOString(),
            })
          }
        />
        <FilterOptionButton
          label="Before last month"
          selected={lastSeenRanges.some((range) =>
            isEqual(range, {
              field: "last_seen",
              lte: subMonths(startOfMonth ?? new Date(), 1).toISOString(),
            }),
          )}
          onToggle={() =>
            toggleLastSeenRange({
              field: "last_seen",
              lte: subMonths(startOfMonth ?? new Date(), 1).toISOString(),
            })
          }
        />
      </FilterCategory>

      <FilterCategory
        label="Archive status"
        icon={<Archive />}
        count={typeof archivedState === "boolean" ? 1 : 0}
        onClear={() => setArchivedFilter(undefined)}
      >
        <FilterOptionButton
          label="Unarchived"
          selected={archivedState === false}
          onToggle={() =>
            setArchivedFilter(archivedState === false ? undefined : false)
          }
        />
        <FilterOptionButton
          label="Archived"
          selected={archivedState === true}
          onToggle={() =>
            setArchivedFilter(archivedState === true ? undefined : true)
          }
        />
      </FilterCategory>

      <FilterCategory
        label="Services interested in"
        icon={<Stethoscope />}
        count={selectedServices.length}
        onClear={clearServices}
      >
        {servicesData && servicesData.length > 0 ? (
          servicesData.map((service) => (
            <FilterOptionButton
              key={service.id}
              label={service.name}
              selected={selectedServices.includes(String(service.id))}
              onToggle={() => toggleService(service.id)}
            />
          ))
        ) : (
          <div className="text-xs text-muted-foreground">
            No services available
          </div>
        )}
      </FilterCategory>

      <FilterCategory
        label="Tags"
        icon={<Tag />}
        count={selectedTags.length}
        onClear={clearTags}
      >
        {data &&
          data.map((record) => (
            <FilterOptionButton
              key={record.id}
              label={
                <Badge
                  variant="secondary"
                  className="text-black text-xs font-normal cursor-pointer"
                  style={{
                    backgroundColor: record?.color,
                  }}
                >
                  {record?.name}
                </Badge>
              }
              selected={selectedTags.includes(String(record.id))}
              onToggle={() => toggleTag(record.id)}
            />
          ))}
      </FilterCategory>

      <FilterCategory
        icon={<CheckSquare />}
        label="Tasks"
        count={hasPendingTasks ? 1 : 0}
        onClear={() => updateFilters({ "nb_tasks@gt": undefined })}
      >
        <FilterOptionButton
          label={"With pending tasks"}
          selected={hasPendingTasks}
          onToggle={toggleTasks}
        />
      </FilterCategory>

      <FilterCategory
        icon={<Users />}
        label="Account Manager"
        count={selectedManagers.length}
        onClear={clearManagers}
      >
        <FilterOptionButton
          label={"Me"}
          selected={selectedManagers.includes(String(identity?.id))}
          onToggle={() => toggleManager(identity?.id)}
        />
        {salesData &&
          salesData
            .filter((manager) => manager.id !== identity?.id)
            .map((manager) => (
              <FilterOptionButton
                key={manager.id}
                label={`${manager.first_name} ${manager.last_name}`}
                selected={selectedManagers.includes(String(manager.id))}
                onToggle={() => toggleManager(manager.id)}
              />
            ))}
      </FilterCategory>
    </div>
  );
};

import { RecordContextProvider, useListContext } from "ra-core";
import { type MouseEvent, useCallback, useRef } from "react";
import { Link } from "react-router";
import { ReferenceField } from "@/components/admin/reference-field";
import { TextField } from "@/components/admin/text-field";
import { Checkbox } from "@/components/ui/checkbox";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { useIsMobile } from "@/hooks/use-mobile";

import type { Contact } from "../types";
import { TagsList } from "../contacts/TagsList";
import { useConfigurationContext } from "../root/ConfigurationContext";
import { formatRelativeOrDate } from "../misc/timezone";

export const ClientListContent = () => {
  const {
    data: clients,
    error,
    isPending,
    onSelect,
    onToggleItem,
    selectedIds,
  } = useListContext<Contact>();
  const isSmall = useIsMobile();
  const lastClickedIndexRef = useRef<number | null>(null);

  // StopPropagation does not work for some reason on Checkbox, this handler is a workaround
  const handleLinkClick = useCallback(function handleLinkClick(
    e: MouseEvent<HTMLAnchorElement>,
  ) {
    // Prevent navigation if clicking on checkbox or if shift key is pressed
    if (
      e.target instanceof HTMLButtonElement ||
      (e.target as HTMLElement).closest('[data-slot="checkbox"]') ||
      e.shiftKey
    ) {
      e.preventDefault();
    }
  }, []);

  const handleCheckboxClick = useCallback(
    (clientId: number | string, index: number, event: MouseEvent) => {
      event.stopPropagation();
      event.preventDefault();

      if (event.shiftKey && lastClickedIndexRef.current !== null) {
        // Shift-click: select range from last clicked to current
        const start = Math.min(lastClickedIndexRef.current, index);
        const end = Math.max(lastClickedIndexRef.current, index);
        const rangeIds = clients
          .slice(start, end + 1)
          .map((client) => client.id);

        // Click would toggle the current item, so apply the *toggled* state to the whole range.
        // Use onSelect (set selected IDs in one shot) to avoid flaky toggling/batching issues.
        const currentIsSelected = selectedIds.includes(clientId);
        const targetIsSelected = !currentIsSelected;

        if (onSelect) {
          const rangeSet = new Set(rangeIds);
          const selectedSet = new Set(selectedIds);
          if (targetIsSelected) {
            rangeIds.forEach((id) => selectedSet.add(id));
          } else {
            selectedIds.forEach((id) => {
              if (rangeSet.has(id)) selectedSet.delete(id);
            });
          }
          onSelect(Array.from(selectedSet));
        } else {
          // Fallback (should rarely happen): toggle items to match target state
          rangeIds.forEach((id) => {
            const isSelected = selectedIds.includes(id);
            if (isSelected !== targetIsSelected) {
              onToggleItem(id);
            }
          });
        }
      } else {
        // Normal click: toggle single item
        onToggleItem(clientId);
      }
      lastClickedIndexRef.current = index;
    },
    [clients, onSelect, onToggleItem, selectedIds],
  );

  if (isPending) {
    return <Skeleton className="w-full h-9" />;
  }

  if (error) {
    return null;
  }
  const now = Date.now();

  return (
    <div className="divide-y">
      {clients.map((client, index) => (
        <RecordContextProvider key={client.id} value={client}>
          <Link
            to={`/clients/${client.id}/show`}
            className="flex flex-row gap-4 items-center px-4 py-2 hover:bg-muted transition-colors first:rounded-t-xl last:rounded-b-xl"
            onClick={handleLinkClick}
          >
            <div 
              onClick={(e) => {
                e.stopPropagation();
                e.preventDefault();
              }}
            >
              <Checkbox
                className="cursor-pointer"
                checked={selectedIds.includes(client.id)}
                // Use mousedown to reliably capture shiftKey with Radix checkbox
                onMouseDown={(e) => handleCheckboxClick(client.id, index, e)}
                // Prevent Radix default click toggling; selection is controlled by react-admin
                onClick={(e) => {
                  e.preventDefault();
                  e.stopPropagation();
                }}
              />
            </div>
            <div className="flex-1 min-w-0">
              <div className="font-medium flex items-center gap-2">
                {`${client.first_name ?? ""} ${client.last_name ?? ""}`.trim() || "New Lead"}
              </div>
              <div className="text-sm text-muted-foreground">
                {client.title}
                {client.title && client.company_id != null && " at "}
                {client.company_id != null && (
                  <ReferenceField
                    source="company_id"
                    reference="companies"
                    link={false}
                  >
                    <TextField source="name" />
                  </ReferenceField>
                )}
                {client.nb_tasks
                  ? ` - ${client.nb_tasks} task${
                      client.nb_tasks > 1 ? "s" : ""
                    }`
                  : ""}
                &nbsp;&nbsp;
                <TagsList />
              </div>
            </div>
            <div className="text-right ml-4 flex items-center gap-2">
              <Badge variant="default" className="text-xs text-white bg-emerald-600 hover:bg-emerald-700">
                Client
              </Badge>
              {client.last_seen && (
                <div
                  className="text-sm text-muted-foreground"
                  title={client.last_seen}
                >
                  {!isSmall && "last activity "}
                  {formatRelativeOrDate(client.last_seen, new Date(now))}
                </div>
              )}
            </div>
          </Link>
        </RecordContextProvider>
      ))}

      {clients.length === 0 && (
        <div className="p-4">
          <div className="text-muted-foreground">No clients found</div>
        </div>
      )}
    </div>
  );
};

