import type { RecurrenceConfig } from "../types";
import { addDays, addWeeks, addMonths, addYears, startOfDay, isBefore, isAfter, format } from "date-fns";

export interface BaseAppointmentData {
  patient_id: number;
  appointment_date: string; // YYYY-MM-DD
  start_time: string; // ISO datetime string
  end_time: string; // ISO datetime string
  duration_minutes: number;
  appointment_type: string;
  status: string;
  notes?: string | null;
  mini_notes?: string | null;
  full_notes?: string | null;
  pickup_instructions?: string | null;
  primary_staff_id?: number | null;
  driver_id?: number | null;
  payment_package_id?: number | null;
}

export interface GeneratedAppointment extends BaseAppointmentData {
  recurrence_sequence: number;
}

const MAX_OCCURRENCES = 1000; // Prevent performance issues

/**
 * Generates recurring appointment instances based on recurrence configuration
 * @param baseAppointment Base appointment data
 * @param recurrenceConfig Recurrence configuration
 * @returns Array of appointment instances with calculated dates/times
 */
export function generateRecurringAppointments(
  baseAppointment: BaseAppointmentData,
  recurrenceConfig: RecurrenceConfig
): GeneratedAppointment[] {
  const appointments: GeneratedAppointment[] = [];

  // Parse the base start and end times
  const baseStartDate = new Date(baseAppointment.start_time);
  const baseEndDate = new Date(baseAppointment.end_time);
  const baseDate = new Date(baseAppointment.appointment_date + "T00:00:00");

  // Determine end date
  let endDate: Date | null = null;
  let maxOccurrences: number | null = null;

  if (recurrenceConfig.end_type === "date" && recurrenceConfig.end_date) {
    endDate = new Date(recurrenceConfig.end_date + "T23:59:59");
  } else if (recurrenceConfig.end_type === "occurrences") {
    // If occurrences is explicitly set, use it; otherwise default to 100
    if (recurrenceConfig.occurrences != null && recurrenceConfig.occurrences > 0) {
      maxOccurrences = Math.min(recurrenceConfig.occurrences, MAX_OCCURRENCES);
    } else {
      // Default: 100 occurrences if end_type is "occurrences" but occurrences not set
      maxOccurrences = 100;
    }
  } else {
    // Default: 100 occurrences if neither specified
    maxOccurrences = 100;
  }

  // Validate end_date is after start_date
  if (endDate && isBefore(endDate, baseStartDate)) {
    throw new Error("End date must be after start date");
  }

  // Validate occurrences > 0
  if (maxOccurrences !== null && maxOccurrences <= 0) {
    throw new Error("Occurrences must be greater than 0");
  }

  let sequence = 0;

  // Always include the first appointment (parent)
  appointments.push({
    ...baseAppointment,
    recurrence_sequence: sequence++,
  });

  // Special handling for weekly pattern with multiple days_of_week
  if (recurrenceConfig.pattern === "weekly" && 
      recurrenceConfig.days_of_week && 
      recurrenceConfig.days_of_week.length > 0) {
    // Generate all appointments for selected days in each week
    let weekOffset = 0;
    const baseDayOfWeek = baseStartDate.getDay();
    const sortedDaysOfWeek = [...recurrenceConfig.days_of_week].sort((a, b) => a - b);

    let shouldStop = false;
    while (true) {
      // Check if we've exceeded max occurrences
      if (maxOccurrences !== null && appointments.length >= maxOccurrences) {
        break;
      }

      // Check if we've generated too many (safety check)
      if (appointments.length >= MAX_OCCURRENCES) {
        break;
      }

      // Calculate the reference date for this week (base date + week offset)
      const weekReferenceDate = addWeeks(baseStartDate, weekOffset * recurrenceConfig.interval);
      const weekReferenceDayOfWeek = weekReferenceDate.getDay();

      // Find the start of the week (Sunday) that contains the reference date
      const weekStartDate = addDays(weekReferenceDate, -weekReferenceDayOfWeek);

      // Generate appointments for all selected days in this week
      let foundAnyInWeek = false;
      let skippedBaseDateInWeek0 = false;

      for (const targetDayOfWeek of sortedDaysOfWeek) {
        // Check BEFORE processing if we've exceeded max occurrences
        if (maxOccurrences !== null && appointments.length >= maxOccurrences) {
          shouldStop = true;
          break;
        }

        // Calculate the target date: start of week + target day of week
        const targetDate = addDays(weekStartDate, targetDayOfWeek);

        // In week 0, skip the base date (already added as parent) and only include days >= base date
        if (weekOffset === 0) {
          if (targetDayOfWeek === baseDayOfWeek) {
            skippedBaseDateInWeek0 = true; // Mark that we skipped the base date
            continue; // Skip base date, already added
          }
          if (isBefore(targetDate, baseStartDate)) {
            continue; // Skip days before base date in week 0
          }
        }

        // Check if this date exceeds end date
        if (endDate && isAfter(targetDate, endDate)) {
          continue;
        }

        // Calculate time difference from base
        const timeDiff = targetDate.getTime() - baseStartDate.getTime();
        const targetEndDate = new Date(baseEndDate.getTime() + timeDiff);

        // Format dates
        const appointmentDate = format(targetDate, "yyyy-MM-dd");
        const startTimeISO = targetDate.toISOString();
        const endTimeISO = targetEndDate.toISOString();

        appointments.push({
          ...baseAppointment,
          appointment_date: appointmentDate,
          start_time: startTimeISO,
          end_time: endTimeISO,
          recurrence_sequence: sequence++,
        });

        foundAnyInWeek = true;

        // Check AFTER adding if we've reached the limit
        if (maxOccurrences !== null && appointments.length >= maxOccurrences) {
          shouldStop = true;
          break;
        }
      }

      // Break out of outer loop if we've reached the limit
      if (shouldStop) {
        break;
      }

      // If we didn't find any valid dates in this week, check if we skipped the base date in week 0
      // If so, we should continue to next week (the parent appointment counts as week 0)
      if (!foundAnyInWeek) {
        if (weekOffset === 0 && skippedBaseDateInWeek0) {
          // Week 0 had the base date which was already added as parent, so continue to next week
          // But only if we haven't reached max occurrences yet
          if (maxOccurrences === null || appointments.length < maxOccurrences) {
            weekOffset++;
            continue;
          }
        }
        // Otherwise, we're done
        break;
      }

      // Move to next week interval
      weekOffset++;

      // Check if next week would exceed end date
      const nextWeekReference = addWeeks(baseStartDate, weekOffset * recurrenceConfig.interval);
      if (endDate && isAfter(nextWeekReference, endDate)) {
        break;
      }
    }
  } else {
    // Standard logic for other patterns (daily, monthly, yearly, etc.)
    let currentDate = new Date(baseStartDate);

    while (true) {
      // Check if we've exceeded max occurrences
      if (maxOccurrences !== null && appointments.length >= maxOccurrences) {
        break;
      }

      // Calculate next date based on pattern
      const nextDate = calculateNextDate(
        currentDate,
        recurrenceConfig,
        baseStartDate
      );

      // Check if next date exceeds end date
      if (endDate && isAfter(nextDate, endDate)) {
        break;
      }

      // Check if we've generated too many (safety check)
      if (appointments.length >= MAX_OCCURRENCES) {
        break;
      }

      // Calculate time difference from base
      const timeDiff = nextDate.getTime() - baseStartDate.getTime();
      const nextEndDate = new Date(baseEndDate.getTime() + timeDiff);

      // Format dates
      const appointmentDate = format(nextDate, "yyyy-MM-dd");
      const startTimeISO = nextDate.toISOString();
      const endTimeISO = nextEndDate.toISOString();

      appointments.push({
        ...baseAppointment,
        appointment_date: appointmentDate,
        start_time: startTimeISO,
        end_time: endTimeISO,
        recurrence_sequence: sequence++,
      });

      currentDate = nextDate;
    }
  }

  return appointments;
}

/**
 * Calculates the next occurrence date based on recurrence pattern
 */
function calculateNextDate(
  currentDate: Date,
  config: RecurrenceConfig,
  baseStartDate: Date
): Date {
  const { pattern, interval, days_of_week, day_of_month, week_of_month, month, custom_unit } = config;

  switch (pattern) {
    case "daily":
      return addDays(currentDate, interval);

    case "weekly":
      if (days_of_week && days_of_week.length > 0) {
        // Find next occurrence of any specified day of week
        return findNextDayOfWeek(currentDate, days_of_week, interval);
      }
      return addWeeks(currentDate, interval);

    case "monthly":
      if (day_of_month !== null && day_of_month !== undefined) {
        // Specific day of month (e.g., 15th of every month)
        return addMonthsToDayOfMonth(currentDate, interval, day_of_month);
      } else if (week_of_month !== null && week_of_month !== undefined && days_of_week && days_of_week.length > 0) {
        // Specific week and day (e.g., first Monday of every month)
        return addMonthsToWeekDay(currentDate, interval, week_of_month, days_of_week[0]);
      }
      return addMonths(currentDate, interval);

    case "yearly":
      if (month !== null && month !== undefined) {
        // Specific month
        if (day_of_month !== null && day_of_month !== undefined) {
          return addYearsToDayOfMonth(currentDate, interval, month, day_of_month);
        } else if (week_of_month !== null && week_of_month !== undefined && days_of_week && days_of_week.length > 0) {
          return addYearsToWeekDay(currentDate, interval, month, week_of_month, days_of_week[0]);
        }
      }
      return addYears(currentDate, interval);

    case "custom":
      if (custom_unit === "days") {
        // If days_of_week is specified, find next occurrence of selected days
        if (days_of_week && days_of_week.length > 0) {
          return findNextDayOfWeek(currentDate, days_of_week, interval);
        }
        return addDays(currentDate, interval);
      } else if (custom_unit === "weeks") {
        // If days_of_week is specified, find next occurrence of selected days
        if (days_of_week && days_of_week.length > 0) {
          return findNextDayOfWeek(currentDate, days_of_week, interval);
        }
        return addWeeks(currentDate, interval);
      } else if (custom_unit === "months") {
        return addMonths(currentDate, interval);
      }
      // Default to days
      return addDays(currentDate, interval);

    default:
      return addDays(currentDate, interval);
  }
}

/**
 * Finds the next occurrence of any specified day of week
 */
function findNextDayOfWeek(currentDate: Date, daysOfWeek: number[], interval: number): Date {
  let nextDate = new Date(currentDate);
  let weeksAdded = 0;
  const maxIterations = interval * 7; // Safety limit

  for (let i = 0; i < maxIterations; i++) {
    nextDate = addDays(nextDate, 1);
    const dayOfWeek = nextDate.getDay();

    if (daysOfWeek.includes(dayOfWeek)) {
      // Check if we've completed the interval
      const weeksSinceStart = Math.floor((nextDate.getTime() - currentDate.getTime()) / (7 * 24 * 60 * 60 * 1000));
      if (weeksSinceStart >= interval) {
        return nextDate;
      }
    }
  }

  // Fallback: add interval weeks
  return addWeeks(currentDate, interval);
}

/**
 * Adds months while preserving day of month (handles month-end edge cases)
 */
function addMonthsToDayOfMonth(currentDate: Date, interval: number, dayOfMonth: number): Date {
  let nextDate = addMonths(currentDate, interval);
  const targetDay = Math.min(dayOfMonth, getDaysInMonth(nextDate));
  nextDate.setDate(targetDay);
  return nextDate;
}

/**
 * Adds months while preserving week and day (e.g., first Monday)
 */
function addMonthsToWeekDay(
  currentDate: Date,
  interval: number,
  weekOfMonth: number,
  dayOfWeek: number
): Date {
  let nextDate = addMonths(currentDate, interval);
  return setWeekDayOfMonth(nextDate, weekOfMonth, dayOfWeek);
}

/**
 * Adds years while preserving month and day of month
 */
function addYearsToDayOfMonth(
  currentDate: Date,
  interval: number,
  month: number,
  dayOfMonth: number
): Date {
  let nextDate = addYears(currentDate, interval);
  nextDate.setMonth(month - 1); // month is 1-12, setMonth expects 0-11
  const targetDay = Math.min(dayOfMonth, getDaysInMonth(nextDate));
  nextDate.setDate(targetDay);
  return nextDate;
}

/**
 * Adds years while preserving month, week, and day
 */
function addYearsToWeekDay(
  currentDate: Date,
  interval: number,
  month: number,
  weekOfMonth: number,
  dayOfWeek: number
): Date {
  let nextDate = addYears(currentDate, interval);
  nextDate.setMonth(month - 1);
  return setWeekDayOfMonth(nextDate, weekOfMonth, dayOfWeek);
}

/**
 * Sets the date to a specific week and day of month (e.g., first Monday)
 */
function setWeekDayOfMonth(date: Date, weekOfMonth: number, dayOfWeek: number): Date {
  // Start from the first day of the month
  const firstDay = new Date(date.getFullYear(), date.getMonth(), 1);

  // Find the first occurrence of the target day of week
  const firstDayOfWeek = firstDay.getDay();
  let daysToAdd = (dayOfWeek - firstDayOfWeek + 7) % 7;

  // Add weeks (weekOfMonth is 1-based: 1 = first, 2 = second, etc.)
  daysToAdd += (weekOfMonth - 1) * 7;

  const targetDate = new Date(firstDay);
  targetDate.setDate(1 + daysToAdd);

  // If the target date is in the next month, go back to the last occurrence
  if (targetDate.getMonth() !== date.getMonth()) {
    targetDate.setDate(targetDate.getDate() - 7);
  }

  return targetDate;
}

/**
 * Gets the number of days in a month
 */
function getDaysInMonth(date: Date): number {
  return new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
}

import React, { useMemo } from "react";
import { useVirtualizer } from "@tanstack/react-virtual";
import { MoreHorizontal, Repeat } from "lucide-react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";
import { format } from "date-fns";
import type { Appointment, PaymentPackage, Service } from "../types";
import { APPOINTMENT_STATUSES } from "./types";
import { useGetList } from "ra-core";
import type { Contact, Staff } from "../types";
import { formatCrmDate } from "../misc/timezone";
import { Link } from "react-router";

type VirtualizedTableProps = {
  appointments: Appointment[];
  loading: boolean;
  onAppointmentClick: (appointment: Appointment) => void;
};

const ITEM_HEIGHT = 80;


export const VirtualizedTable: React.FC<VirtualizedTableProps> = ({
  appointments,
  loading,
  onAppointmentClick,
}) => {
  const parentRef = React.useRef<HTMLDivElement>(null);
  const { data: patientsData } = useGetList<Contact>("clients", {
    pagination: { page: 1, perPage: 1000 },
  });
  const { data: staffData } = useGetList<Staff>("staff", {
    pagination: { page: 1, perPage: 1000 },
  });

  // Get unique payment package IDs from appointments
  const packageIds = useMemo(() => {
    const ids = appointments
      .map(a => a.payment_package_id)
      .filter((id): id is number => id !== null && id !== undefined);
    return [...new Set(ids)];
  }, [appointments]);

  // Fetch payment packages
  const { data: paymentPackages } = useGetList<PaymentPackage>("payment_packages", {
    pagination: { page: 1, perPage: 1000 },
    filter: packageIds.length > 0 ? { id: packageIds } : { id: [] },
  }, { enabled: packageIds.length > 0 });

  const { data: services } = useGetList<Service>("services", {
    pagination: { page: 1, perPage: 1000 },
  });

  // Create a map for quick lookup
  const packageMap = useMemo(() => {
    const map = new Map<number, PaymentPackage>();
    paymentPackages?.forEach(pkg => {
      if (pkg.id) map.set(Number(pkg.id), pkg);
    });
    return map;
  }, [paymentPackages]);

  const sortedAppointments = useMemo(() => {
    return [...appointments].sort((a, b) => {
      // Parse dates - start_time is an ISO datetime string
      let dateA: Date;
      let dateB: Date;

      try {
        if (a.start_time) {
          dateA = new Date(a.start_time);
        } else if (a.appointment_date) {
          dateA = new Date(a.appointment_date + "T00:00:00");
        } else {
          dateA = new Date(0); // Fallback to epoch
        }

        if (b.start_time) {
          dateB = new Date(b.start_time);
        } else if (b.appointment_date) {
          dateB = new Date(b.appointment_date + "T00:00:00");
        } else {
          dateB = new Date(0); // Fallback to epoch
        }

        // Validate dates
        if (isNaN(dateA.getTime())) dateA = new Date(0);
        if (isNaN(dateB.getTime())) dateB = new Date(0);

        return dateA.getTime() - dateB.getTime();
      } catch (error) {
        console.warn("Error sorting appointments:", error);
        return 0;
      }
    });
  }, [appointments]);

  const virtualizer = useVirtualizer({
    count: sortedAppointments.length,
    getScrollElement: () => parentRef.current,
    estimateSize: () => ITEM_HEIGHT,
    overscan: 5,
  });

  const getStatusBadge = (status: string) => {
    const statusConfig = APPOINTMENT_STATUSES.find((s) => s.value === status);
    if (!statusConfig) {
      return (
        <Badge className="bg-[--muted] text-[--muted-foreground]">
          {status}
        </Badge>
      );
    }

    const variantMap: Record<string, "default" | "secondary" | "destructive" | "outline"> = {
      scheduled: "secondary",
      cancelled: "destructive",
      completed: "default",
    };

    return (
      <Badge
        variant={variantMap[status] || "outline"}
        className={`${
          status === "scheduled"
            ? "bg-[--warning]/10 text-[--warning]"
            : status === "cancelled"
            ? "bg-[--error]/10 text-[--error]"
            : status === "completed"
            ? "bg-[--primary]/10 text-[--primary]"
            : ""
        } inline-flex items-center px-2.5 py-1 rounded-full text-xs font-medium`}
      >
        {statusConfig.label}
      </Badge>
    );
  };

  const getPatientName = (patientId: string | number) => {
    const patient = patientsData?.find((p) => p.id === patientId);
    if (!patient) return "Unknown";
    return `${patient.first_name} ${patient.last_name}`.trim();
  };

  const getPatientInitials = (patientId: string | number) => {
    const patient = patientsData?.find((p) => p.id === patientId);
    if (!patient) return "U";
    const first = patient.first_name?.[0] || "";
    const last = patient.last_name?.[0] || "";
    return (first + last).toUpperCase() || "U";
  };

  const formatAppointmentType = (type: string) => {
    return type
      .split("_")
      .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
      .join(" ");
  };

  if (loading) {
    return (
      <Card className="bg-[--card] border border-[--border] rounded-xl shadow-lg p-6">
        <div className="space-y-4">
          {Array.from({ length: 5 }).map((_, i) => (
            <Skeleton key={i} className="h-20 w-full" />
          ))}
        </div>
      </Card>
    );
  }

  if (sortedAppointments.length === 0) {
    return (
      <Card className="bg-[--card] border border-[--border] rounded-xl shadow-lg p-6">
        <div className="text-center py-12 text-[--muted-foreground]">
          No appointments found
        </div>
      </Card>
    );
  }

  return (
    <Card className="bg-[--card] border border-[--border] rounded-xl shadow-lg overflow-hidden">
      <div className="overflow-x-auto">
        <div className="min-w-full">
          {/* Table Header */}
          <div className="grid grid-cols-[150px_200px_150px_100px_120px_150px_200px_100px] gap-4 px-4 py-3 border-b border-[--border] bg-[--muted]/50">
            <div className="text-xs font-semibold text-[--muted-foreground] uppercase">
              Time
            </div>
            <div className="text-xs font-semibold text-[--muted-foreground] uppercase">
              Patient
            </div>
            <div className="text-xs font-semibold text-[--muted-foreground] uppercase">
              Type
            </div>
            <div className="text-xs font-semibold text-[--muted-foreground] uppercase">
              Duration
            </div>
            <div className="text-xs font-semibold text-[--muted-foreground] uppercase">
              Status
            </div>
            <div className="text-xs font-semibold text-[--muted-foreground] uppercase">
              Package
            </div>
            <div className="text-xs font-semibold text-[--muted-foreground] uppercase">
              Notes
            </div>
            <div className="text-xs font-semibold text-[--muted-foreground] uppercase">
              Actions
            </div>
          </div>

          {/* Virtualized Rows */}
          <div
            ref={parentRef}
            className="overflow-y-auto"
            style={{ height: "600px" }}
          >
            <div
              style={{
                height: `${virtualizer.getTotalSize()}px`,
                width: "100%",
                position: "relative",
              }}
            >
              {virtualizer.getVirtualItems().map((virtualRow) => {
                const appointment = sortedAppointments[virtualRow.index];

                // Parse dates - start_time and end_time are ISO datetime strings
                let startTime: Date;
                let endTime: Date;

                try {
                  if (appointment.start_time) {
                    startTime = new Date(appointment.start_time);
                  } else if (appointment.appointment_date) {
                    startTime = new Date(appointment.appointment_date + "T00:00:00");
                  } else {
                    startTime = new Date();
                  }

                  if (appointment.end_time) {
                    endTime = new Date(appointment.end_time);
                  } else if (appointment.appointment_date) {
                    endTime = new Date(appointment.appointment_date + "T01:00:00");
                  } else {
                    endTime = new Date();
                  }

                  // Validate dates
                  if (isNaN(startTime.getTime())) startTime = new Date();
                  if (isNaN(endTime.getTime())) endTime = new Date();
                } catch (error) {
                  console.warn("Error parsing appointment times:", error);
                  startTime = new Date();
                  endTime = new Date();
                }

                return (
                  <div
                    key={virtualRow.key}
                    data-index={virtualRow.index}
                    ref={virtualizer.measureElement}
                    style={{
                      position: "absolute",
                      top: 0,
                      left: 0,
                      width: "100%",
                      height: `${virtualRow.size}px`,
                      transform: `translateY(${virtualRow.start}px)`,
                    }}
                    className="grid grid-cols-[150px_200px_150px_100px_120px_150px_200px_100px] gap-4 px-4 py-3 border-b border-[--border] hover:bg-[--accent] cursor-pointer items-center"
                    onClick={() => onAppointmentClick(appointment)}
                  >
                    {/* Time */}
                    <div className="flex items-center gap-2">
                      <div>
                        <div className="text-sm text-[--foreground] font-medium">
                          {format(startTime, "HH:mm")} - {format(endTime, "HH:mm")}
                        </div>
                        <div className="text-xs text-[--muted-foreground]">
                          {formatCrmDate(appointment.appointment_date)}
                        </div>
                      </div>
                    </div>

                    {/* Patient */}
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 rounded-full bg-[--muted] flex items-center justify-center text-xs font-medium text-[--foreground]">
                        {getPatientInitials(appointment.patient_id)}
                      </div>
                      <span className="text-sm text-[--foreground]">
                        {getPatientName(appointment.patient_id)}
                      </span>
                    </div>

                    {/* Type */}
                    <div className="text-sm text-[--foreground]">
                      {formatAppointmentType(appointment.appointment_type)}
                    </div>

                    {/* Duration */}
                    <div className="text-sm text-[--foreground]">
                      {appointment.duration_minutes} min
                    </div>

                    {/* Status */}
                    <div>{getStatusBadge(appointment.status)}</div>

                    {/* Package */}
                    <div>
                      {appointment.payment_package_id ? (() => {
                        const pkg = packageMap.get(Number(appointment.payment_package_id));
                        if (!pkg) return <span className="text-xs text-[--muted-foreground]">Loading...</span>;
                        const service = services?.find(s => s.id === pkg.service_id);
                        const statusColors = {
                          active: "bg-green-100 text-green-800 dark:bg-green-900/30 dark:text-green-300",
                          completed: "bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-300",
                          expired: "bg-red-100 text-red-800 dark:bg-red-900/30 dark:text-red-300",
                        };
                        return (
                          <Link
                            to={`/payment_packages/${pkg.id}/show`}
                            onClick={(e) => e.stopPropagation()}
                            className="flex items-center gap-1 hover:underline"
                          >
                            <span className="text-xs text-[--foreground]">
                              #{pkg.id}
                            </span>
                            <Badge className={`${statusColors[pkg.status] || statusColors.completed} text-xs`}>
                              {pkg.status}
                            </Badge>
                          </Link>
                        );
                      })() : (
                        <span className="text-xs text-[--muted-foreground]">-</span>
                      )}
                    </div>

                    {/* Notes */}
                    <div className="text-sm text-[--foreground] truncate">
                      {appointment.notes || appointment.mini_notes || "No notes"}
                    </div>

                    {/* Actions */}
                    <div>
                      <Button
                        variant="ghost"
                        size="sm"
                        className="p-2 text-[--muted-foreground] hover:text-[--foreground] hover:bg-[--accent] rounded-lg transition-colors"
                        onClick={(e) => {
                          e.stopPropagation();
                          // TODO: Open actions menu
                        }}
                      >
                        <MoreHorizontal className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </Card>
  );
};

import React, { useState, useRef, useEffect } from "react";
import { Check, ChevronDown, X } from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";

export type MultiSelectOption = {
  value: string;
  label: string;
  color?: string;
  icon?: React.ReactNode;
  serviceId?: string | number; // Optional service ID for unique identification
};

type CustomMultiSelectProps = {
  options: MultiSelectOption[];
  selected: string[];
  onChange: (selected: string[]) => void;
  placeholder: string;
  className?: string;
};

export const CustomMultiSelect: React.FC<CustomMultiSelectProps> = ({
  options,
  selected,
  onChange,
  placeholder,
  className,
}) => {
  const [isOpen, setIsOpen] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (containerRef.current && !containerRef.current.contains(event.target as Node)) {
        setIsOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const handleSelectAll = () => {
    if (!options || !Array.isArray(options)) return;
    if (selected.length === options.length) {
      onChange([]);
    } else {
      onChange(options.map((opt) => opt.value));
    }
  };

  const handleToggle = (value: string) => {
    if (selected.includes(value)) {
      onChange(selected.filter((v) => v !== value));
    } else {
      onChange([...selected, value]);
    }
  };

  const getDisplayText = () => {
    if (!options || !Array.isArray(options) || selected.length === 0) return placeholder;
    if (selected.length === 1) {
      const option = options.find((opt) => opt.value === selected[0]);
      return option?.label || placeholder;
    }
    if (selected.length === options.length) {
      return `All ${placeholder}`;
    }
    return `${selected.length} selected`;
  };

  const getVisibleIndicators = () => {
    if (!options || !Array.isArray(options) || selected.length === 0) return [];
    const selectedOptions = options.filter((opt) => selected.includes(opt.value));
    return selectedOptions.slice(0, 2);
  };

  const visibleIndicators = getVisibleIndicators();
  const remainingCount = (selected?.length || 0) - visibleIndicators.length;

  return (
    <div ref={containerRef} className={cn("relative", className)}>
      <Button
        type="button"
        variant="outline"
        onClick={() => setIsOpen(!isOpen)}
        className={cn(
          "w-full px-3 py-2 text-left bg-white dark:bg-slate-800 border border-slate-300 dark:border-slate-600 rounded-lg hover:border-blue-500 dark:hover:border-blue-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 text-sm justify-between transition-colors",
          isOpen && "border-blue-500 dark:border-blue-400",
          className
        )}
      >
        <div className="flex items-center gap-2 flex-1 min-w-0">
          {visibleIndicators.length > 0 && (
            <div className="flex items-center gap-1 flex-shrink-0">
              {visibleIndicators.map((opt, idx) => (
                <div
                  key={opt.serviceId !== undefined ? `${opt.value}-${opt.serviceId}` : `${opt.value}-${idx}`}
                  className="w-2 h-2 rounded-full flex-shrink-0"
                  style={{ backgroundColor: opt.color || "#6b7280" }}
                />
              ))}
              {remainingCount > 0 && (
                <span className="text-xs text-[--muted-foreground]">+{remainingCount}</span>
              )}
            </div>
          )}
          <span className={cn(
            "truncate text-slate-700 dark:text-slate-200",
            visibleIndicators.length > 0 && "ml-2"
          )}>
            {getDisplayText()}
          </span>
        </div>
        <ChevronDown
          className={cn(
            "w-4 h-4 text-slate-500 dark:text-slate-400 flex-shrink-0 transition-all",
            isOpen && "transform rotate-180 text-blue-600 dark:text-blue-400"
          )}
        />
      </Button>

      {isOpen && (
        <div className="absolute z-50 mt-1 w-full bg-white dark:bg-slate-800 border border-slate-200 dark:border-slate-700 rounded-lg shadow-xl overflow-hidden">
          <button
            type="button"
            onClick={handleSelectAll}
            className="w-full flex items-center gap-2 px-3 py-2.5 text-sm hover:bg-blue-50 dark:hover:bg-slate-700 text-left border-b border-slate-200 dark:border-slate-700 transition-colors"
          >
            <Check
              className={cn(
                "w-4 h-4 flex-shrink-0",
                options && Array.isArray(options) && selected.length === options.length
                  ? "text-blue-600 dark:text-blue-400"
                  : "text-slate-400 dark:text-slate-500"
              )}
            />
            <span className="text-slate-700 dark:text-slate-200 font-medium">Select All</span>
          </button>
          <div className="max-h-60 overflow-y-auto">
            {(options || []).map((option, index) => {
              const isSelected = selected.includes(option.value);
              // Use serviceId if available for unique key, otherwise use value + index
              const uniqueKey = option.serviceId !== undefined 
                ? `${option.value}-${option.serviceId}` 
                : `${option.value}-${index}`;
              return (
                <button
                  key={uniqueKey}
                  type="button"
                  onClick={() => handleToggle(option.value)}
                  className={cn(
                    "w-full flex items-center gap-2.5 px-3 py-2.5 text-sm text-left transition-colors",
                    isSelected
                      ? "bg-blue-50 dark:bg-blue-900/20 hover:bg-blue-100 dark:hover:bg-blue-900/30"
                      : "hover:bg-slate-50 dark:hover:bg-slate-700/50"
                  )}
                >
                  <div className="flex items-center gap-2 flex-1 min-w-0">
                    {option.color && (
                      <div
                        className="w-3 h-3 rounded-full flex-shrink-0 border border-slate-200 dark:border-slate-600"
                        style={{ backgroundColor: option.color }}
                      />
                    )}
                    {option.icon && <span className="flex-shrink-0 text-slate-600 dark:text-slate-300">{option.icon}</span>}
                    <span className={cn(
                      "flex-1 text-slate-700 dark:text-slate-200",
                      isSelected && "font-medium"
                    )}>
                      {option.label}
                    </span>
                  </div>
                  {isSelected && (
                    <Check className="w-4 h-4 flex-shrink-0 text-blue-600 dark:text-blue-400" />
                  )}
                </button>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

import React, { useState, useMemo, useCallback, useEffect } from "react";
import { Grid3X3, List, Plus, Map, ChevronLeft, ChevronRight, Calendar as CalendarIcon, X } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useGetList, useNotify, useDelete, useDataProvider } from "ra-core";
import { AppointmentFiltersTop } from "./AppointmentFiltersTop";
import { AppointmentCalendar } from "./AppointmentCalendar";
