    </div>
  );
};

import React, { useState, useMemo } from "react";
import { useForm } from "react-hook-form";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectGroup, SelectItem, SelectLabel, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Command, CommandEmpty, CommandGroup, CommandInput, CommandItem, CommandList } from "@/components/ui/command";
import { Check, ChevronsUpDown, Search } from "lucide-react";
import { useGetList } from "ra-core";
import type { Appointment, Contact, Staff, RecurrenceConfig, PaymentPackage, Service } from "../types";
import { APPOINTMENT_STATUSES } from "./types";
import { useAppointmentTypes } from "./useAppointmentTypes";
import { formatCrmTime, crmDateYmdInputString, extractCrmTime } from "../misc/timezone";
import { cn } from "@/lib/utils";
import { CustomMultiSelect, type MultiSelectOption } from "./CustomMultiSelect";

type AppointmentFormProps = {
  appointment?: Appointment | null;
  initialDateTime?: { start: Date; end: Date } | null;
  onSubmit: (data: any) => void;
  isSubmitting: boolean;
};

type AppointmentFormData = {
  patient_id: string;
  appointment_date: string;
  start_time: string;
  end_time: string;
  duration_minutes: number;
  appointment_type: string[]; // Changed to array for multiple types
  status: string;
  notes?: string;
  mini_notes?: string;
  full_notes?: string;
  pickup_instructions?: string;
  primary_staff_id?: string;
  driver_id?: string;
  staff_ids?: string[]; // Already an array for multiple staff
  payment_package_id?: string;
  // Recurrence fields
  is_recurring?: boolean;
  recurrence_pattern?: "daily" | "weekly" | "monthly" | "yearly" | "custom";
  recurrence_interval?: number;
  recurrence_end_type?: "date" | "occurrences";
  recurrence_end_date?: string;
  recurrence_occurrences?: number;
  recurrence_days_of_week?: number[];
  recurrence_day_of_month?: number;
  recurrence_week_of_month?: number;
  recurrence_month?: number;
  recurrence_custom_unit?: "days" | "weeks" | "months";
};

export const AppointmentForm: React.FC<AppointmentFormProps> = ({
  appointment,
  initialDateTime,
  onSubmit,
  isSubmitting,
}) => {
  const { data: patients } = useGetList<Contact>("clients", {
    pagination: { page: 1, perPage: 1000 },
  });
  const { data: staff } = useGetList<Staff>("staff", {
    pagination: { page: 1, perPage: 1000 },
  });
  const appointmentTypes = useAppointmentTypes();
  const { data: services } = useGetList<Service>("services", {
    pagination: { page: 1, perPage: 1000 },
  });

  // Helper to convert appointment type to service IDs for form
  // Priority: Use selected_service_ids from custom_fields if available
  const convertAppointmentTypeToServiceIds = React.useCallback((appointmentType: string | string[] | undefined, customFields?: Record<string, any>): string[] => {
    // First, check if we have selected service IDs stored in custom_fields
    const selectedServiceIds = customFields?.selected_service_ids;
    if (Array.isArray(selectedServiceIds) && selectedServiceIds.length > 0) {
      // Convert service IDs to service_ format
      return selectedServiceIds.map(id => `service_${id}`);
    }

    if (!appointmentType) return [];
    const types = Array.isArray(appointmentType) ? appointmentType : [appointmentType];

    // If it's already in service_ format, return as is
    if (types.some(t => typeof t === "string" && t.startsWith("service_"))) {
      return types.filter(t => typeof t === "string" && t.startsWith("service_")) as string[];
    }

    // Otherwise, find services that match this appointment type
    return appointmentTypes
      .filter(type => type.appointmentType === types[0] || types.includes(type.appointmentType || ""))
      .map(type => type.value);
  }, [appointmentTypes]);

  const {
    register,
    handleSubmit,
    watch,
    setValue,
    formState: { errors },
  } = useForm<AppointmentFormData>({
    defaultValues: appointment
      ? {
          patient_id: appointment.patient_id.toString(),
          appointment_date: appointment.appointment_date.split("T")[0],
          // Extract time in CRM timezone, not UTC
          // appointment.start_time and end_time are ISO strings stored in UTC
          // We need to format them in the configured CRM timezone for display
          start_time: extractCrmTime(appointment.start_time) || "",
          end_time: extractCrmTime(appointment.end_time) || "",
          duration_minutes: appointment.duration_minutes,
          appointment_type: convertAppointmentTypeToServiceIds(appointment.appointment_type, appointment.custom_fields),
          status: appointment.status,
          notes: appointment.notes || "",
          mini_notes: appointment.mini_notes || "",
          full_notes: appointment.full_notes || "",
          pickup_instructions: appointment.pickup_instructions || "",
          primary_staff_id: appointment.primary_staff_id?.toString() || "",
          driver_id: appointment.driver_id?.toString() || "",
          staff_ids: appointment.staff_ids?.map((id) => id.toString()) || [],
          payment_package_id: appointment.payment_package_id?.toString() || "",
        }
      : (() => {
          // Use initialDateTime if provided, otherwise use current date/time
          if (initialDateTime) {
            const start = initialDateTime.start;
            const end = initialDateTime.end;

            // Ensure start and end are Date objects
            const startDate = start instanceof Date ? start : new Date(start);
            const endDate = end instanceof Date ? end : new Date(end);

            // Validate dates - check if they're valid Date objects
            if (!(startDate instanceof Date && endDate instanceof Date) || 
                isNaN(startDate.getTime()) || 
                isNaN(endDate.getTime()) ||
                startDate.getTime() === 0 ||
                endDate.getTime() === 0) {
              // Silently use defaults instead of logging
              return {
                appointment_date: new Date().toISOString().split("T")[0],
                status: "scheduled",
                duration_minutes: 60,
                start_time: "",
                end_time: "",
                patient_id: "",
                appointment_type: [],
                primary_staff_id: "",
                driver_id: "",
                staff_ids: [],
                is_recurring: false,
                recurrence_pattern: "daily",
                recurrence_interval: 1,
                recurrence_end_type: "occurrences",
                recurrence_occurrences: 10,
              };
            }

            const durationMs = endDate.getTime() - startDate.getTime();
            const durationMinutes = Math.max(Math.round(durationMs / (60 * 1000)), 15);

            // Format date as YYYY-MM-DD using timezone utility
            const dateStr = crmDateYmdInputString(startDate) || new Date().toISOString().split("T")[0];

            // Format time as HH:MM using timezone-aware extractor
            // FullCalendar with timeZone set displays times in the configured CRM timezone,
            // but returns Date objects in the browser's local timezone.
            // We need to extract the time components as they appear in the CRM timezone.
            const startTimeStr = extractCrmTime(startDate);
            const endTimeStr = extractCrmTime(endDate);

          return {
            appointment_date: dateStr,
            start_time: startTimeStr || "",
            end_time: endTimeStr || "",
            duration_minutes: durationMinutes,
            status: "scheduled",
            appointment_type: [],
            primary_staff_id: "",
            driver_id: "",
            staff_ids: [],
          };
          }
          return {
            appointment_date: new Date().toISOString().split("T")[0],
            status: "scheduled",
            duration_minutes: 60,
            start_time: "",
            end_time: "",
            patient_id: "",
            appointment_type: [],
            primary_staff_id: "",
            driver_id: "",
            staff_ids: [],
            is_recurring: false,
            recurrence_pattern: "daily",
            recurrence_interval: 1,
            recurrence_end_type: "occurrences",
            recurrence_occurrences: 10,
          };
        })(),
  });

  const isRecurring = watch("is_recurring");
  const recurrencePattern = watch("recurrence_pattern") || "daily";
  const recurrenceEndType = watch("recurrence_end_type") || "occurrences";
  const recurrenceDaysOfWeek = watch("recurrence_days_of_week") || [];

  // Patient combobox state
  const [patientOpen, setPatientOpen] = useState(false);
  const [patientSearch, setPatientSearch] = useState("");
  const selectedPatientId = watch("patient_id");
  const selectedAppointmentTypes = watch("appointment_type") || [];

  // Fetch payment packages for selected patient
  const { data: paymentPackages } = useGetList<PaymentPackage>("payment_packages", {
    pagination: { page: 1, perPage: 1000 },
    filter: selectedPatientId ? { patient_id: selectedPatientId, status: "active" } : {},
    sort: { field: "created_at", order: "DESC" },
  }, { enabled: !!selectedPatientId });

  // Create patients map for quick lookup
  const patientsMap = useMemo(() => {
    if (!patients) return new Map<number, Contact>();
    return new Map(patients.map((p) => [Number(p.id), p]));
  }, [patients]);

  // Filter packages that match the selected appointment type/service
  const availablePackages = useMemo(() => {
    if (!paymentPackages || !services || selectedAppointmentTypes.length === 0) {
      return [];
    }

    // Get service IDs from selected appointment types
    const serviceIds = selectedAppointmentTypes
      .filter(type => type.startsWith("service_"))
      .map(type => type.replace("service_", ""));

    // Also get services that match appointment types
    const matchingServices = appointmentTypes
      .filter(type => selectedAppointmentTypes.includes(type.value))
      .map(type => type.serviceId?.toString())
      .filter(Boolean);

    const allServiceIds = [...new Set([...serviceIds, ...matchingServices])];

    // Filter packages that have matching service_id or no service_id (for post-payment)
    return paymentPackages.filter(pkg => {
      if (!pkg.service_id) return true; // Post-payment packages can be linked to any appointment
      return allServiceIds.includes(pkg.service_id.toString());
    });
  }, [paymentPackages, services, selectedAppointmentTypes, appointmentTypes]);

  // Filter patients based on search
  const filteredPatients = useMemo(() => {
    if (!patients) return [];
    if (!patientSearch) return patients;
    const searchLower = patientSearch.toLowerCase();
    return patients.filter((patient) => {
      const fullName = `${patient.first_name} ${patient.last_name}`.toLowerCase();
      return fullName.includes(searchLower) || 
             patient.first_name?.toLowerCase().includes(searchLower) ||
             patient.last_name?.toLowerCase().includes(searchLower);
    });
  }, [patients, patientSearch]);

  const selectedPatient = patients?.find((p) => p.id.toString() === selectedPatientId);

  const startTime = watch("start_time");
  const durationMinutes = watch("duration_minutes");
  const appointmentDate = watch("appointment_date");
  const hasSetFromCalendar = React.useRef(false);


  // Update form values when initialDateTime is provided (for new appointments from calendar drag)
  React.useEffect(() => {
    if (!appointment && initialDateTime) {
      const start = initialDateTime.start;
      const end = initialDateTime.end;

      // Ensure start and end are Date objects
      const startDate = start instanceof Date ? start : new Date(start);
      const endDate = end instanceof Date ? end : new Date(end);

      // Validate dates - check if they're valid Date objects
      if (!(startDate instanceof Date && endDate instanceof Date) || 
          isNaN(startDate.getTime()) || 
          isNaN(endDate.getTime()) ||
          startDate.getTime() === 0 ||
          endDate.getTime() === 0) {
        // Silently skip update instead of logging
        return;
      }

      const durationMs = endDate.getTime() - startDate.getTime();
      const durationMinutes = Math.max(Math.round(durationMs / (60 * 1000)), 15);

      // Format date and time in CRM timezone
      const dateStr = crmDateYmdInputString(startDate) || `${startDate.getFullYear()}-${String(startDate.getMonth() + 1).padStart(2, "0")}-${String(startDate.getDate()).padStart(2, "0")}`;
      const startTimeStr = extractCrmTime(startDate);
      const endTimeStr = extractCrmTime(endDate);

      hasSetFromCalendar.current = true;
      setValue("appointment_date", dateStr, { shouldValidate: false, shouldDirty: false });
      setValue("start_time", startTimeStr, { shouldValidate: false, shouldDirty: false });
      setValue("end_time", endTimeStr, { shouldValidate: false, shouldDirty: false });
      setValue("duration_minutes", durationMinutes, { shouldValidate: false, shouldDirty: false });

      // Reset the flag after a short delay to allow auto-calculation on user changes
      setTimeout(() => {
        hasSetFromCalendar.current = false;
      }, 100);
    }
  }, [initialDateTime, appointment, setValue]);

  // Auto-calculate end_time when start_time or duration changes
  // Skip if we just set values from calendar selection to preserve the selected time range
  React.useEffect(() => {
    if (startTime && durationMinutes && !hasSetFromCalendar.current) {
      const [hours, minutes] = startTime.split(":").map(Number);
      const start = new Date();
      start.setHours(hours, minutes, 0, 0);
      const end = new Date(start.getTime() + durationMinutes * 60 * 1000);
      const endTimeStr = `${end.getHours().toString().padStart(2, "0")}:${end
        .getMinutes()
        .toString()
        .padStart(2, "0")}`;
      // Only set if end_time is not already set or if it would be different
      const currentEndTime = watch("end_time");
      if (!currentEndTime || currentEndTime !== endTimeStr) {
        setValue("end_time", endTimeStr, { shouldValidate: false });
      }
    }
  }, [startTime, durationMinutes, setValue, watch]);

  const onFormSubmit = (data: AppointmentFormData) => {
    // Convert "__none__" back to empty string for payment_package_id
    if (data.payment_package_id === "__none__") {
      data.payment_package_id = "";
    }
    onSubmit(data);
  };

  // Generate time options with 15-minute intervals
  const timeOptions = useMemo(() => {
    const options = [];
    for (let hour = 0; hour < 24; hour++) {
      for (let minute = 0; minute < 60; minute += 15) {
        const timeString = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
        const displayHour = hour === 0 ? 12 : hour > 12 ? hour - 12 : hour;
        const displayTime = `${displayHour}:${minute.toString().padStart(2, '0')} ${hour >= 12 ? 'PM' : 'AM'}`;
        options.push({ value: timeString, label: displayTime });
      }
    }
    return options;
  }, []);

  return (
    <form id="appointment-form" onSubmit={handleSubmit(onFormSubmit)} className="space-y-5">
      {/* Patient Selection */}
      <div>
        <Label htmlFor="patient_id" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
          Patient <span className="text-red-500">*</span>
        </Label>
        <Popover open={patientOpen} onOpenChange={setPatientOpen}>
          <PopoverTrigger asChild>
            <Button
              variant="outline"
              role="combobox"
              aria-expanded={patientOpen}
              className="w-full h-9 justify-between rounded-lg border-slate-300 dark:border-slate-600 bg-white dark:bg-slate-800 hover:bg-slate-50 dark:hover:bg-slate-700 font-normal text-sm"
            >
              {selectedPatient ? (
                <span className="truncate">
                  {selectedPatient.first_name} {selectedPatient.last_name}
                </span>
              ) : (
                <span className="text-slate-500 dark:text-slate-400">Search for a patient...</span>
              )}
              <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
            </Button>
          </PopoverTrigger>
          <PopoverContent className="p-0" align="start" sideOffset={4} style={{ width: 'var(--radix-popover-trigger-width)' }}>
            <Command className="rounded-lg border-0">
              <div className="flex items-center border-b px-3">
                <Search className="mr-2 h-4 w-4 shrink-0 opacity-50" />
                <CommandInput
                  placeholder="Search patients..."
                  value={patientSearch}
                  onValueChange={setPatientSearch}
                  className="border-0 focus:ring-0"
                />
              </div>
              <CommandList className="max-h-[300px]">
                <CommandEmpty>No patient found.</CommandEmpty>
                <CommandGroup>
                  {filteredPatients.map((patient) => (
                    <CommandItem
                      key={patient.id}
                      value={`${patient.first_name} ${patient.last_name}`}
                      onSelect={() => {
                        setValue("patient_id", patient.id.toString());
                        setPatientOpen(false);
                        setPatientSearch("");
                      }}
                      className="cursor-pointer"
                    >
                      <Check
                        className={cn(
                          "mr-2 h-4 w-4",
                          selectedPatientId === patient.id.toString() ? "opacity-100" : "opacity-0"
                        )}
                      />
                      <div className="flex flex-col">
                        <span className="font-medium">
                          {patient.first_name} {patient.last_name}
                        </span>
                        {patient.email && (
                          <span className="text-xs text-slate-500 dark:text-slate-400">
                            {patient.email}
                          </span>
                        )}
                      </div>
                    </CommandItem>
                  ))}
                </CommandGroup>
              </CommandList>
            </Command>
          </PopoverContent>
        </Popover>
        {errors.patient_id && (
          <p className="text-xs text-red-600 dark:text-red-400 mt-1">Patient is required</p>
        )}
      </div>

      {/* Row 2: Date, Start, End, Duration */}
      <div className="grid grid-cols-4 gap-3">
        <div>
          <Label htmlFor="appointment_date" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
            Date <span className="text-red-500">*</span>
          </Label>
          <Input
            type="date"
            {...register("appointment_date", { required: true })}
            className="h-9 rounded-lg border-slate-300 dark:border-slate-600 bg-white dark:bg-slate-800 text-sm"
          />
          {errors.appointment_date && (
            <p className="text-xs text-red-600 dark:text-red-400 mt-1">Required</p>
          )}
        </div>
        <div>
          <Label htmlFor="start_time" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
            Start <span className="text-red-500">*</span>
          </Label>
          <Select
            value={watch("start_time") || ""}
            onValueChange={(value) => setValue("start_time", value)}
          >
            <SelectTrigger className="h-9 rounded-lg border-slate-300 dark:border-slate-600 bg-white dark:bg-slate-800 text-sm">
              <SelectValue placeholder="--:--" />
            </SelectTrigger>
            <SelectContent className="max-h-[300px]">
              {timeOptions.map((time) => (
                <SelectItem key={time.value} value={time.value}>
                  {time.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
          {errors.start_time && (
            <p className="text-xs text-red-600 dark:text-red-400 mt-1">Required</p>
          )}
        </div>
        <div>
          <Label htmlFor="end_time" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
            End <span className="text-red-500">*</span>
          </Label>
          <Select
            value={watch("end_time") || ""}
            onValueChange={(value) => setValue("end_time", value)}
          >
            <SelectTrigger className="h-9 rounded-lg border-slate-300 dark:border-slate-600 bg-white dark:bg-slate-800 text-sm">
              <SelectValue placeholder="--:--" />
            </SelectTrigger>
            <SelectContent className="max-h-[300px]">
              {timeOptions.map((time) => (
                <SelectItem key={time.value} value={time.value}>
                  {time.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
          {errors.end_time && (
            <p className="text-xs text-red-600 dark:text-red-400 mt-1">Required</p>
          )}
        </div>
        <div>
          <Label htmlFor="duration_minutes" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
            Duration <span className="text-red-500">*</span>
          </Label>
          <Input
            type="number"
            {...register("duration_minutes", { required: true, min: 15 })}
            className="h-9 rounded-lg border-slate-300 dark:border-slate-600 bg-white dark:bg-slate-800 text-sm"
            placeholder="60"
            step="15"
            min="15"
          />
          {errors.duration_minutes && (
            <p className="text-xs text-red-600 dark:text-red-400 mt-1">Min 15 min</p>
          )}
        </div>
      </div>

      {/* Row 3: Type, Status */}
      <div className="grid grid-cols-2 gap-3">
        <div>
          <Label htmlFor="appointment_type" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
            Type <span className="text-red-500">*</span>
          </Label>
          <CustomMultiSelect
            options={appointmentTypes.map((type) => ({
              value: type.value,
              label: type.label,
              color: type.color,
              serviceId: type.serviceId,
            }))}
            selected={watch("appointment_type") || []}
            onChange={(selected) => {
              setValue("appointment_type", selected);
              // Clear payment package if appointment type changes and package doesn't match
              const currentPackageId = watch("payment_package_id");
              if (currentPackageId && availablePackages.length > 0) {
                const currentPackage = availablePackages.find(p => p.id.toString() === currentPackageId);
                if (!currentPackage) {
                  setValue("payment_package_id", "");
                }
              }
            }}
            placeholder="Select types"
            className="h-9"
          />
          {errors.appointment_type && (
            <p className="text-xs text-red-600 dark:text-red-400 mt-1">Required</p>
          )}
        </div>
        <div>
          <Label htmlFor="status" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
            Status <span className="text-red-500">*</span>
          </Label>
          <Select
            value={watch("status") || ""}
            onValueChange={(value) => setValue("status", value)}
          >
            <SelectTrigger className="h-9 rounded-lg border-slate-300 dark:border-slate-600 bg-white dark:bg-slate-800 text-sm">
              <SelectValue placeholder="Select" />
            </SelectTrigger>
            <SelectContent>
              {APPOINTMENT_STATUSES.map((status) => (
                <SelectItem key={status.value} value={status.value}>
                  {status.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      {/* Payment Package Link */}
      {selectedPatientId && (
        <div>
          <Label htmlFor="payment_package_id" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
            Link to Payment Package (Optional)
          </Label>
          <Select
            value={watch("payment_package_id") || "__none__"}
            onValueChange={(value) => {
              if (value && value !== "__none__") {
                const selectedPackage = availablePackages.find(p => p.id.toString() === value);
                if (selectedPackage) {
                  // Validate service type matches
                  const packageService = services?.find(s => s.id === selectedPackage.service_id);
                  if (packageService && selectedAppointmentTypes.length > 0) {
                    const matches = selectedAppointmentTypes.some(type => {
                      const serviceId = type.replace("service_", "");
                      return serviceId === packageService.id.toString() || 
                             appointmentTypes.some(at => at.value === type && at.serviceId === packageService.id);
                    });
                    if (!matches && selectedPackage.package_type !== "post-payment") {
                      setValue("payment_package_id", "");
                      alert(`This package is for ${packageService.name}, which doesn't match the selected appointment type.`);
                      return;
                    }
                  }
                  setValue("payment_package_id", value);
                }
              } else {
                setValue("payment_package_id", "");
              }
            }}
          >
            <SelectTrigger className="h-9 rounded-lg border-slate-300 dark:border-slate-600 bg-white dark:bg-slate-800 text-sm">
              <SelectValue placeholder="Select package (optional)" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="__none__">None</SelectItem>
              {availablePackages.length > 0 ? (
                availablePackages.map((pkg) => {
                  // Get patient name
                  const patient = patientsMap.get(Number(pkg.patient_id));
                  const patientName = patient 
                    ? `${patient.first_name} ${patient.last_name}`.trim()
                    : `Patient #${pkg.patient_id}`;

                  // Use package name if available, otherwise build from service
                  const serviceName = services?.find(s => s.id === pkg.service_id)?.name || "Service";
                  const packageName = (pkg as any).name || `${serviceName} Package`;

                  // Build label: "Package Name - Patient Name - Status"
                  const packageLabel = `${packageName} - ${patientName} - ${pkg.status}`;

                  return (
                    <SelectItem key={pkg.id} value={pkg.id.toString()}>
                      {packageLabel}
                    </SelectItem>
                  );
                })
              ) : selectedAppointmentTypes.length > 0 ? (
                <SelectGroup>
                  <SelectLabel>No matching packages found</SelectLabel>
                </SelectGroup>
              ) : (
                <SelectGroup>
                  <SelectLabel>Select appointment type first</SelectLabel>
                </SelectGroup>
              )}
            </SelectContent>
          </Select>
          {availablePackages.length > 0 && watch("payment_package_id") && (
            <p className="text-xs text-slate-500 dark:text-slate-400 mt-1">
              Package will be used to track usage for this appointment
            </p>
          )}
        </div>
      )}

      {/* Row 4: Staff, Driver */}
      <div className="grid grid-cols-2 gap-3">
        <div>
          <Label htmlFor="staff_ids" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
            Staff
          </Label>
          <CustomMultiSelect
            options={staff?.filter((s) => {
              // Filter out Management and Driver staff types
              const staffTypeLower = s.staff_type?.toLowerCase() || "";
              return s.first_name && 
                     s.last_name && 
                     !staffTypeLower.includes("management") && 
                     !staffTypeLower.includes("driver");
            }).map((s) => ({
              value: s.id.toString(),
              label: `${s.first_name.trim()} ${s.last_name.trim()}`.trim(),
            })) || []}
            selected={watch("staff_ids") || []}
            onChange={(selected) => setValue("staff_ids", selected)}
            placeholder="Select staff"
            className="h-9"
          />
        </div>
        <div>
          <Label htmlFor="driver_id" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
            Driver
          </Label>
          <Select
            value={watch("driver_id") || ""}
            onValueChange={(value) => setValue("driver_id", value || "")}
          >
            <SelectTrigger className="h-9 rounded-lg border-slate-300 dark:border-slate-600 bg-white dark:bg-slate-800 text-sm">
              <SelectValue placeholder="Optional" />
            </SelectTrigger>
            <SelectContent>
              {staff?.filter((s) => s.staff_type.toLowerCase().includes("driver")).map((s) => (
                <SelectItem key={s.id} value={s.id.toString()}>
                  {s.first_name} {s.last_name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      {/* Recurrence Configuration */}
      <div className="space-y-4 pt-4 border-t border-slate-200 dark:border-slate-700">
        <div className="flex items-center space-x-2">
          <Checkbox
            id="is_recurring"
            checked={isRecurring || false}
            onCheckedChange={(checked) => {
              setValue("is_recurring", checked as boolean);
              if (!checked) {
                // Reset recurrence fields when disabled
                setValue("recurrence_pattern", "daily");
                setValue("recurrence_interval", 1);
                setValue("recurrence_end_type", "occurrences");
                setValue("recurrence_occurrences", 10);
                setValue("recurrence_end_date", "");
                setValue("recurrence_days_of_week", []);
              }
            }}
            className="w-5 h-5 rounded-md border-slate-300 dark:border-slate-600"
          />
          <Label htmlFor="is_recurring" className="text-base font-medium text-slate-900 dark:text-slate-100 cursor-pointer">
            Make this a recurring appointment
          </Label>
        </div>

        {isRecurring && (
          <div className="space-y-3 pl-4 border-l-2 border-purple-200 dark:border-purple-800 bg-purple-50/20 dark:bg-purple-950/10 rounded-r-lg p-4">
            {/* Pattern and Interval - Compact Grid */}
            <div className="grid grid-cols-3 gap-3">
              <div className="col-span-2">
                <Label htmlFor="recurrence_pattern" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
                  Pattern <span className="text-red-500">*</span>
                </Label>
                <Select
                  value={recurrencePattern}
                  onValueChange={(value) => {
                    setValue("recurrence_pattern", value as any);
                    setValue("recurrence_days_of_week", []);
                    setValue("recurrence_day_of_month", undefined);
                    setValue("recurrence_week_of_month", undefined);
                    setValue("recurrence_month", undefined);
                  }}
                >
                  <SelectTrigger className="h-9 rounded-lg border-slate-300 dark:border-slate-600 bg-white dark:bg-slate-800 text-sm">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="daily">Daily</SelectItem>
                    <SelectItem value="weekly">Weekly</SelectItem>
                    <SelectItem value="monthly">Monthly</SelectItem>
                    <SelectItem value="yearly">Yearly</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="recurrence_interval" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
                  Every
                </Label>
                <div className="flex gap-1">
                  <Input
                    type="number"
                    min="1"
                    value={watch("recurrence_interval") || 1}
                    onChange={(e) => setValue("recurrence_interval", parseInt(e.target.value) || 1)}
                    className="h-9 rounded-lg border-slate-300 dark:border-slate-600 bg-white dark:bg-slate-800 text-sm flex-1"
                  />
                  <div className="flex items-center text-xs text-slate-600 dark:text-slate-400 px-2">
                    {recurrencePattern === "daily" && "day(s)"}
                    {recurrencePattern === "weekly" && "week(s)"}
                    {recurrencePattern === "monthly" && "month(s)"}
                    {recurrencePattern === "yearly" && "year(s)"}
                  </div>
                </div>
              </div>
            </div>

            {/* Weekly: Days of Week */}
            {recurrencePattern === "weekly" && (
              <div>
                <Label className="text-xs font-medium text-slate-600 dark:text-slate-400 mb-2 block">
                  Days
                </Label>
                <div className="flex flex-wrap gap-2">
                  {[
                    { value: 1, label: "Mo" },
                    { value: 2, label: "Tu" },
                    { value: 3, label: "We" },
                    { value: 4, label: "Th" },
                    { value: 5, label: "Fr" },
                    { value: 6, label: "Sa" },
                    { value: 0, label: "Su" },
                  ].map((day) => (
                    <button
                      key={day.value}
                      type="button"
                      onClick={() => {
                        const current = recurrenceDaysOfWeek || [];
                        if (recurrenceDaysOfWeek.includes(day.value)) {
                          setValue(
                            "recurrence_days_of_week",
                            current.filter((d) => d !== day.value)
                          );
                        } else {
                          setValue("recurrence_days_of_week", [...current, day.value]);
                        }
                      }}
                      className={`px-3 py-1.5 rounded-lg text-xs font-medium transition-all ${
                        recurrenceDaysOfWeek.includes(day.value)
                          ? "bg-blue-600 text-white shadow-sm"
                          : "bg-white dark:bg-slate-800 border border-slate-300 dark:border-slate-600 text-slate-700 dark:text-slate-300 hover:border-blue-400"
                      }`}
                    >
                      {day.label}
                    </button>
                  ))}
                </div>
              </div>
            )}


            {/* Monthly: Day of Month */}
            {recurrencePattern === "monthly" && (
              <div>
                <Label htmlFor="recurrence_day_of_month">Day of Month</Label>
                <Input
                  type="number"
                  min="1"
                  max="31"
                  placeholder="e.g., 15"
                  value={watch("recurrence_day_of_month") || ""}
                  onChange={(e) =>
                    setValue("recurrence_day_of_month", parseInt(e.target.value) || undefined)
                  }
                />
                <p className="text-xs text-gray-500 mt-1">
                  Leave empty to use the same day as start date
                </p>
              </div>
            )}

            {/* Yearly: Month */}
            {recurrencePattern === "yearly" && (
              <div>
                <Label htmlFor="recurrence_month">Month</Label>
                <Select
                  value={watch("recurrence_month")?.toString() || ""}
                  onValueChange={(value) => setValue("recurrence_month", parseInt(value) || undefined)}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select month" />
                  </SelectTrigger>
                  <SelectContent>
                    {[
                      { value: 1, label: "January" },
                      { value: 2, label: "February" },
                      { value: 3, label: "March" },
                      { value: 4, label: "April" },
                      { value: 5, label: "May" },
                      { value: 6, label: "June" },
                      { value: 7, label: "July" },
                      { value: 8, label: "August" },
                      { value: 9, label: "September" },
                      { value: 10, label: "October" },
                      { value: 11, label: "November" },
                      { value: 12, label: "December" },
                    ].map((month) => (
                      <SelectItem key={month.value} value={month.value.toString()}>
                        {month.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            )}

            {/* End Condition - Compact */}
            <div className="grid grid-cols-2 gap-2">
              <div className="flex items-center gap-2 p-2 bg-white dark:bg-slate-800 rounded-lg border border-slate-200 dark:border-slate-700">
                <input
                  type="radio"
                  id="end_type_occurrences"
                  name="recurrence_end_type"
                  value="occurrences"
                  checked={recurrenceEndType === "occurrences"}
                  onChange={() => setValue("recurrence_end_type", "occurrences")}
                  className="w-4 h-4 text-blue-600"
                />
                <Label htmlFor="end_type_occurrences" className="cursor-pointer flex items-center gap-2 text-sm flex-1">
                  <span>After</span>
                  <Input
                    type="number"
                    min="1"
                    max="1000"
                    className="h-7 w-16 rounded border-slate-300 dark:border-slate-600 text-xs"
                    value={watch("recurrence_occurrences") || 10}
                    onChange={(e) =>
                      setValue("recurrence_occurrences", parseInt(e.target.value) || 10)
                    }
                    disabled={recurrenceEndType !== "occurrences"}
                  />
                </Label>
              </div>
              <div className="flex items-center gap-2 p-2 bg-white dark:bg-slate-800 rounded-lg border border-slate-200 dark:border-slate-700">
                <input
                  type="radio"
                  id="end_type_date"
                  name="recurrence_end_type"
                  value="date"
                  checked={recurrenceEndType === "date"}
                  onChange={() => setValue("recurrence_end_type", "date")}
                  className="w-4 h-4 text-blue-600"
                />
                <Label htmlFor="end_type_date" className="cursor-pointer flex items-center gap-2 text-sm flex-1">
                  <span>On</span>
                  <Input
                    type="date"
                    className="h-7 flex-1 rounded border-slate-300 dark:border-slate-600 text-xs"
                    value={watch("recurrence_end_date") || ""}
                    onChange={(e) => setValue("recurrence_end_date", e.target.value)}
                    disabled={recurrenceEndType !== "date"}
                    min={appointmentDate}
                  />
                </Label>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Notes Section - Compact */}
      <div className="space-y-3 pt-4 border-t border-slate-200 dark:border-slate-700">
        <div>
          <Label htmlFor="mini_notes" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
            Brief Notes
          </Label>
          <Textarea
            {...register("mini_notes")}
            placeholder="Brief notes..."
            rows={2}
            className="rounded-lg border-slate-300 dark:border-slate-600 bg-white dark:bg-slate-800 resize-none text-sm"
          />
        </div>
        <div>
          <Label htmlFor="full_notes" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
            Detailed Notes
          </Label>
          <Textarea
            {...register("full_notes")}
            placeholder="Detailed notes..."
            rows={3}
            className="rounded-lg border-slate-300 dark:border-slate-600 bg-white dark:bg-slate-800 resize-none text-sm"
          />
        </div>
        <div>
          <Label htmlFor="pickup_instructions" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
            Pickup Instructions
          </Label>
          <Textarea
            {...register("pickup_instructions")}
            placeholder="Special instructions..."
            rows={2}
            className="rounded-lg border-slate-300 dark:border-slate-600 bg-white dark:bg-slate-800 resize-none text-sm"
          />
        </div>
      </div>
    </form>
  );
};

import React, { useMemo, useState } from "react";
import { Filter, X, ChevronDown, ChevronUp, Calendar } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { CustomMultiSelect, type MultiSelectOption } from "./CustomMultiSelect";
import type { AppointmentFilterState } from "./types";
import { APPOINTMENT_STATUSES } from "./types";
import { Clock, CheckCircle, XCircle } from "lucide-react";
import type { Staff } from "../types";
import { useGetList } from "ra-core";
import { useAppointmentTypes } from "./useAppointmentTypes";

type AppointmentFiltersTopProps = {
  filters: AppointmentFilterState;
  onFiltersChange: (filters: AppointmentFilterState) => void;
  onApply: () => void;
};

const StatusIconMap: Record<string, React.ReactNode> = {
  scheduled: <Clock className="w-3 h-3" />,
  completed: <CheckCircle className="w-3 h-3" />,
  cancelled: <XCircle className="w-3 h-3" />,
};

export const AppointmentFiltersTop: React.FC<AppointmentFiltersTopProps> = ({
  filters,
  onFiltersChange,
  onApply,
}) => {
  const [isExpanded, setIsExpanded] = useState(false);

  const { data: staffData } = useGetList<Staff>("staff", {
    pagination: { page: 1, perPage: 1000 },
  }, {
    retry: false,
    enabled: true,
    onError: (error) => {
      console.warn("Failed to load staff for filters:", error);
    },
  });

  const appointmentTypes = useAppointmentTypes();

  const activeFilterCount = useMemo(() => {
    let count = 0;
    if (filters.staffIds.length > 0) count++;
    if (filters.appointmentTypes.length > 0) count++;
    if (filters.statuses.length > 0) count++;
    if (filters.dateFrom || filters.dateTo) count++;
    return count;
  }, [filters]);

  const staffOptions: MultiSelectOption[] = useMemo(() => {
    if (!staffData) return [];
    return staffData.map((staff) => ({
      value: staff.id.toString(),
      label: `${staff.first_name} ${staff.last_name} (${staff.staff_type})`,
      color: "#6b7280",
    }));
  }, [staffData]);

  const appointmentTypeOptions: MultiSelectOption[] = useMemo(() => {
    return appointmentTypes.map((type) => ({
      value: type.value,
      label: type.label,
      color: type.color,
      serviceId: type.serviceId,
    }));
  }, [appointmentTypes]);

  const statusOptions: MultiSelectOption[] = useMemo(() => {
    return APPOINTMENT_STATUSES.map((status) => ({
      value: status.value,
      label: status.label,
      color: status.color,
      icon: StatusIconMap[status.value],
    }));
  }, []);

  const handleClear = () => {
    onFiltersChange({
      staffIds: [],
      appointmentTypes: [],
      statuses: [],
      dateFrom: null,
      dateTo: null,
    });
  };

  return (
    <div className="bg-transparent">
      {/* Collapsed Header - Single Row */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2 flex-1 min-w-0">
          <Button
            type="button"
            variant="ghost"
            size="sm"
            onClick={() => setIsExpanded(!isExpanded)}
            className="h-8 px-2"
          >
            <Filter className="w-4 h-4 mr-1" />
            Filters
            {activeFilterCount > 0 && (
              <Badge variant="secondary" className="ml-1 h-5 px-1.5 text-xs">
                {activeFilterCount}
              </Badge>
            )}
            {isExpanded ? (
              <ChevronUp className="w-4 h-4 ml-1" />
            ) : (
              <ChevronDown className="w-4 h-4 ml-1" />
            )}
          </Button>

          {/* Active filters summary when collapsed */}
          {!isExpanded && activeFilterCount > 0 && (
            <div className="flex items-center gap-1 flex-wrap">
              {filters.staffIds.length > 0 && (
                <Badge variant="outline" className="text-xs">
                  {filters.staffIds.length} staff
                </Badge>
              )}
              {filters.appointmentTypes.length > 0 && (
                <Badge variant="outline" className="text-xs">
                  {filters.appointmentTypes.length} types
                </Badge>
              )}
              {filters.statuses.length > 0 && (
                <Badge variant="outline" className="text-xs">
                  {filters.statuses.length} statuses
                </Badge>
              )}
              {(filters.dateFrom || filters.dateTo) && (
                <Badge variant="outline" className="text-xs">
                  Date range
                </Badge>
              )}
            </div>
          )}
        </div>

        {!isExpanded && activeFilterCount > 0 && (
          <Button
            type="button"
            variant="ghost"
            size="sm"
            onClick={handleClear}
            className="h-8 px-2 text-xs"
          >
            <X className="w-3 h-3 mr-1" />
            Clear
          </Button>
        )}
      </div>

      {/* Expanded Content */}
      {isExpanded && (
        <div className="border-t border-slate-200 dark:border-slate-700 p-4 space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {/* Staff Filter */}
            <div>
              <label className="text-xs font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
                Staff
              </label>
              <CustomMultiSelect
                options={staffOptions}
                selectedValues={filters.staffIds}
                onChange={(values) =>
                  onFiltersChange({ ...filters, staffIds: values })
                }
                placeholder="Select staff"
              />
            </div>

            {/* Appointment Type Filter */}
            <div>
              <label className="text-xs font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
                Appointment Type
              </label>
              <CustomMultiSelect
                options={appointmentTypeOptions}
                selectedValues={filters.appointmentTypes}
                onChange={(values) =>
                  onFiltersChange({ ...filters, appointmentTypes: values })
                }
                placeholder="Select types"
              />
            </div>

            {/* Status Filter */}
            <div>
              <label className="text-xs font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
                Status
              </label>
              <CustomMultiSelect
                options={statusOptions}
                selectedValues={filters.statuses}
                onChange={(values) =>
                  onFiltersChange({ ...filters, statuses: values })
                }
                placeholder="Select statuses"
              />
            </div>

            {/* Date Range */}
            <div>
              <label className="text-xs font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
                Date Range
              </label>
              <div className="space-y-1.5">
                <div className="relative">
                  <Calendar className="absolute left-2 top-1/2 -translate-y-1/2 w-3 h-3 text-slate-400 pointer-events-none" />
                  <Input
                    type="date"
                    value={filters.dateFrom || ""}
                    onChange={(e) =>
                      onFiltersChange({ ...filters, dateFrom: e.target.value || null })
                    }
                    className="w-full pl-8 pr-2 py-1.5 text-xs border border-slate-300 dark:border-slate-600 rounded-lg bg-white dark:bg-slate-800 text-slate-900 dark:text-slate-100"
                    placeholder="From date"
                  />
                </div>
                <div className="relative">
                  <Calendar className="absolute left-2 top-1/2 -translate-y-1/2 w-3 h-3 text-slate-400 pointer-events-none" />
                  <Input
                    type="date"
                    value={filters.dateTo || ""}
                    onChange={(e) =>
                      onFiltersChange({ ...filters, dateTo: e.target.value || null })
                    }
                    className="w-full pl-8 pr-2 py-1.5 text-xs border border-slate-300 dark:border-slate-600 rounded-lg bg-white dark:bg-slate-800 text-slate-900 dark:text-slate-100"
                    placeholder="To date"
                  />
                </div>
              </div>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex items-center justify-between pt-2 border-t border-slate-200 dark:border-slate-700">
            <Button
              type="button"
              variant="ghost"
              size="sm"
              onClick={handleClear}
              className="text-xs"
            >
              <X className="w-3 h-3 mr-1" />
              Clear All
            </Button>
            <div className="flex items-center gap-2">
              <Button
                type="button"
                variant="outline"
                size="sm"
                onClick={() => setIsExpanded(false)}
                className="text-xs"
              >
                Cancel
              </Button>
              <Button
                type="button"
                size="sm"
                onClick={() => {
                  onApply();
                  setIsExpanded(false);
                }}
                className="text-xs"
              >
                Apply Filters
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

import React, { useMemo } from "react";
import { Filter, X, Calendar } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { CustomMultiSelect, type MultiSelectOption } from "./CustomMultiSelect";
import type { AppointmentFilterState } from "./types";
import { APPOINTMENT_STATUSES } from "./types";
import { Clock, CheckCircle, XCircle } from "lucide-react";
import type { Staff } from "../types";
import { useGetList } from "ra-core";
import { useAppointmentTypes } from "./useAppointmentTypes";

type AppointmentFiltersProps = {
  filters: AppointmentFilterState;
  onFiltersChange: (filters: AppointmentFilterState) => void;
  onApply: () => void;
};

const StatusIconMap: Record<string, React.ReactNode> = {
  scheduled: <Clock className="w-3 h-3" />,
  completed: <CheckCircle className="w-3 h-3" />,
  cancelled: <XCircle className="w-3 h-3" />,
};

// Sidebar filter component - always visible, not collapsible
export const AppointmentFiltersSidebar: React.FC<AppointmentFiltersProps> = ({
  filters,
  onFiltersChange,
  onApply,
}) => {
  const { data: staffData } = useGetList<Staff>("staff", {
    pagination: { page: 1, perPage: 1000 },
  }, {
    retry: false,
    enabled: true,
    onError: (error) => {
      console.warn("Failed to load staff for filters:", error);
    },
  });

  const appointmentTypes = useAppointmentTypes();

  const activeFilterCount = useMemo(() => {
    let count = 0;
    if (filters.staffIds.length > 0) count++;
    if (filters.appointmentTypes.length > 0) count++;
    if (filters.statuses.length > 0) count++;
    if (filters.dateFrom || filters.dateTo) count++;
    return count;
  }, [filters]);

  const staffOptions: MultiSelectOption[] = useMemo(() => {
    if (!staffData) return [];
    return staffData.map((staff) => ({
      value: staff.id.toString(),
      label: `${staff.first_name} ${staff.last_name} (${staff.staff_type})`,
      color: "#6b7280",
    }));
  }, [staffData]);

  const appointmentTypeOptions: MultiSelectOption[] = useMemo(() => {
    return appointmentTypes.map((type) => ({
      value: type.value,
      label: type.label,
      color: type.color,
      serviceId: type.serviceId,
    }));
  }, [appointmentTypes]);

  const statusOptions: MultiSelectOption[] = useMemo(() => {
    return APPOINTMENT_STATUSES.map((status) => ({
      value: status.value,
      label: status.label,
      color: status.color,
      icon: StatusIconMap[status.value],
    }));
  }, []);

  const handleClear = () => {
    onFiltersChange({
      staffIds: [],
      appointmentTypes: [],
      statuses: [],
      dateFrom: null,
      dateTo: null,
    });
  };

  return (
    <Card className="bg-white dark:bg-slate-800/50 backdrop-blur-sm rounded-2xl shadow-sm border border-slate-200 dark:border-slate-700 h-full flex flex-col">
      <CardHeader className="px-3 py-3 border-b border-slate-200 dark:border-slate-700 flex-shrink-0">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-1.5">
            <Filter className="w-4 h-4 text-blue-600 dark:text-blue-400" />
            <span className="text-sm font-semibold text-slate-900 dark:text-slate-100">Filters</span>
            {activeFilterCount > 0 && (
              <Badge className="bg-blue-600 text-white text-xs font-medium px-1.5 py-0.5 rounded-full">
                {activeFilterCount}
              </Badge>
            )}
          </div>
          {activeFilterCount > 0 && (
            <Button
              variant="ghost"
              size="sm"
              onClick={handleClear}
              className="h-6 w-6 p-0 hover:bg-slate-100 dark:hover:bg-slate-700"
            >
              <X className="w-3 h-3" />
            </Button>
          )}
        </div>
      </CardHeader>

      <CardContent className="p-3 flex-1 overflow-y-auto">
        <div className="space-y-3">
          {/* Staff Member Filter */}
          <div>
            <label className="text-xs font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
              Staff Member
            </label>
            <CustomMultiSelect
              options={staffOptions}
              selected={filters.staffIds}
              onChange={(selected) =>
                onFiltersChange({ ...filters, staffIds: selected })
              }
              placeholder="Select staff..."
            />
          </div>

          {/* Appointment Type Filter */}
          <div>
            <label className="text-xs font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
              Appointment Type
            </label>
            <CustomMultiSelect
              options={appointmentTypeOptions}
              selected={filters.appointmentTypes}
              onChange={(selected) =>
                onFiltersChange({ ...filters, appointmentTypes: selected })
              }
              placeholder="Select type..."
            />
          </div>

          {/* Status Filter */}
          <div>
            <label className="text-xs font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
              Status
            </label>
            <CustomMultiSelect
              options={statusOptions}
              selected={filters.statuses}
              onChange={(selected) =>
                onFiltersChange({ ...filters, statuses: selected })
              }
              placeholder="Select status..."
            />
          </div>

          {/* Date Range Filters */}
          <div>
            <label className="text-xs font-medium text-slate-700 dark:text-slate-300 mb-1.5 block">
              Date Range
            </label>
            <div className="space-y-1.5">
              <div className="relative">
                <Calendar className="absolute left-2 top-1/2 -translate-y-1/2 w-3 h-3 text-slate-400 pointer-events-none" />
                <Input
                  type="date"
                  value={filters.dateFrom || ""}
                  onChange={(e) =>
                    onFiltersChange({ ...filters, dateFrom: e.target.value || null })
                  }
                  className="w-full pl-8 pr-2 py-1.5 text-xs border border-slate-300 dark:border-slate-600 rounded-lg bg-white dark:bg-slate-800 text-slate-900 dark:text-slate-100 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  placeholder="From date"
                />
              </div>
              <div className="relative">
                <Calendar className="absolute left-2 top-1/2 -translate-y-1/2 w-3 h-3 text-slate-400 pointer-events-none" />
                <Input
                  type="date"
                  value={filters.dateTo || ""}
                  onChange={(e) =>
                    onFiltersChange({ ...filters, dateTo: e.target.value || null })
                  }
                  className="w-full pl-8 pr-2 py-1.5 text-xs border border-slate-300 dark:border-slate-600 rounded-lg bg-white dark:bg-slate-800 text-slate-900 dark:text-slate-100 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  placeholder="To date"
                />
              </div>
            </div>
          </div>
        </div>
      </CardContent>

      {/* Apply Button - Fixed at bottom */}
      <div className="px-3 py-3 border-t border-slate-200 dark:border-slate-700 flex-shrink-0">
        <Button
          onClick={onApply}
          className="w-full px-3 py-2 bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white rounded-lg shadow-md hover:shadow-lg transition-all duration-200 text-sm font-medium"
        >
          Apply Filters
        </Button>
      </div>
    </Card>
  );
};

import React from "react";
import { X, User, Calendar, Clock, Stethoscope, Car, FileText, MapPin, Phone, ExternalLink, Printer, Copy, Edit, Trash2, CreditCard } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { format, parseISO } from "date-fns";
import type { Appointment, PaymentPackage, Service } from "../types";
import { APPOINTMENT_STATUSES } from "./types";
import { useAppointmentTypes } from "./useAppointmentTypes";
import { useGetList } from "ra-core";
import type { Contact, Staff } from "../types";
import { formatCrmDate, formatCrmTime } from "../misc/timezone";
import { Link } from "react-router";
import { usePackageUsage } from "@/hooks/usePackageUsage";

type AppointmentDetailsDrawerProps = {
  open: boolean;
  onClose: () => void;
  appointment: Appointment | null;
  onEdit: (appointment: Appointment) => void;
  onDelete: (appointment: Appointment) => void;
};

export const AppointmentDetailsDrawer: React.FC<AppointmentDetailsDrawerProps> = ({
  open,
  onClose,
  appointment,
  onEdit,
  onDelete,
}) => {
  // All hooks must be called before any early returns
  const { data: patients } = useGetList<Contact>("clients", {
    pagination: { page: 1, perPage: 1000 },
  });
  const { data: staff } = useGetList<Staff>("staff", {
    pagination: { page: 1, perPage: 1000 },
  });
  const appointmentTypes = useAppointmentTypes();

  // Fetch payment package if linked
  const { data: paymentPackages } = useGetList<PaymentPackage>("payment_packages", {
    pagination: { page: 1, perPage: 1 },
    filter: appointment?.payment_package_id ? { id: appointment.payment_package_id } : {},
  }, { enabled: !!appointment?.payment_package_id });

  const paymentPackage = paymentPackages?.[0];

  // Fetch service for payment package
  const { data: services } = useGetList<Service>("services", {
    pagination: { page: 1, perPage: 1000 },
  });

  // Get package usage if package exists
  const { sessionsUsed, hoursUsed } = usePackageUsage(paymentPackage?.id);

  // Get all assigned staff from staff_ids array
  const assignedStaff = React.useMemo(() => {
    if (!staff || !appointment?.staff_ids || appointment.staff_ids.length === 0) return [];
    return appointment.staff_ids
      .map((staffId) => staff.find((s) => s.id === staffId))
      .filter((s): s is Staff => s !== undefined);
  }, [staff, appointment?.staff_ids]);

  // Find selected appointment types - use selected_service_ids from custom_fields if available
  // Otherwise fall back to showing all services that match the appointment_type
  const appointmentTypesList = React.useMemo(() => {
    if (!appointment?.appointment_type) return [];

    // First, check if we have selected service IDs stored in custom_fields
    const selectedServiceIds = appointment?.custom_fields?.selected_service_ids;
    if (Array.isArray(selectedServiceIds) && selectedServiceIds.length > 0) {
      // Show only the selected services
      return appointmentTypes.filter((t) => {
        const serviceType = t as any;
        return selectedServiceIds.includes(serviceType.serviceId);
      });
    }

    // Fallback: The database stores the mapped appointment_type (e.g., "doctor_on_call")
    // Find all services that map to this appointment_type (for backward compatibility)
    return appointmentTypes.filter((t) => t.appointmentType === appointment.appointment_type);
  }, [appointmentTypes, appointment?.appointment_type, appointment?.custom_fields]);

  // For backward compatibility, also try to find by direct value match
  const appointmentType = React.useMemo(() => {
    if (!appointment?.appointment_type) return undefined;
    // First try to find by appointment_type directly (for old data)
    let found = appointmentTypes.find((t) => t.appointmentType === appointment.appointment_type);
    if (!found) {
      // Try to find by value (for new service ID format)
      found = appointmentTypes.find((t) => t.value === appointment.appointment_type);
    }
    // If still not found and appointment_type looks like a service ID, try to match
    if (!found && typeof appointment.appointment_type === "string" && appointment.appointment_type.startsWith("service_")) {
      const serviceId = appointment.appointment_type.replace("service_", "");
      found = appointmentTypes.find((t) => t.serviceId?.toString() === serviceId);
    }
    return found || appointmentTypesList[0]; // Fallback to first matching type
  }, [appointmentTypes, appointment?.appointment_type, appointmentTypesList]);

  // Early return after all hooks
  if (!appointment) return null;

  const patient = patients?.find((p) => p.id === appointment.patient_id);
  const primaryStaff = staff?.find((s) => s.id === appointment.primary_staff_id);
  const driver = staff?.find((s) => s.id === appointment.driver_id);
  const status = APPOINTMENT_STATUSES.find((s) => s.value === appointment.status);

  // Parse times using the timezone utility to ensure correct Dubai timezone display
  const startTimeDate = appointment.start_time ? new Date(appointment.start_time) : null;
  const endTimeDate = appointment.end_time ? new Date(appointment.end_time) : null;
  const startTimeStr = startTimeDate ? formatCrmTime(startTimeDate) : "";
  const endTimeStr = endTimeDate ? formatCrmTime(endTimeDate) : "";

  const handlePrint = () => {
    window.open(`/print/appointment/${appointment.id}`, "_blank");
  };

  const handleCopy = () => {
    // TODO: Implement copy functionality
    console.log("Copy appointment:", appointment.id);
  };

  return (
    <>
      {/* Backdrop */}
      {open && (
        <div
          className="fixed inset-0 bg-black/50 backdrop-blur-sm z-40 transition-opacity"
          onClick={onClose}
        />
      )}

      {/* Drawer */}
      <div
        className={`fixed right-0 top-0 h-full w-full max-w-2xl bg-white shadow-2xl z-50 transition-transform duration-200 ease-in-out ${
          open ? "translate-x-0" : "translate-x-full"
        }`}
      >
        {/* Header */}
        <div className="bg-gray-50 border-b border-gray-200 p-6">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-bold text-gray-900">Appointment Details</h2>
              {appointmentTypesList.length > 0 ? (
                <div className="flex flex-wrap gap-2 mt-1">
                  {appointmentTypesList.map((type) => (
                    <Badge
                      key={type.value}
                      style={{
                        backgroundColor: type.color + "20",
                        color: type.color,
                      }}
                      className="text-xs"
                    >
                      {type.label}
                    </Badge>
                  ))}
                </div>
              ) : appointmentType ? (
                <p className="text-gray-600 mt-1">{appointmentType.label}</p>
              ) : null}
            </div>
            <Button variant="ghost" size="sm" onClick={onClose}>
              <X className="w-5 h-5" />
            </Button>
          </div>
        </div>

        {/* Content */}
        <div className="overflow-y-auto h-[calc(100%-200px)] p-6 space-y-6">
          {/* Patient Information */}
          <div className="bg-gray-50 rounded-lg p-4">
            <div className="flex items-center gap-2 mb-3">
              <User className="w-5 h-5 text-gray-600" />
              <h3 className="text-lg font-semibold">Patient Information</h3>
            </div>
            {patient && (
              <div className="space-y-2">
                <p className="font-medium">{patient.first_name} {patient.last_name}</p>
                {patient.phone_jsonb && patient.phone_jsonb.length > 0 && (
                  <div className="flex items-center gap-2 text-sm text-gray-600">
                    <Phone className="w-4 h-4" />
                    <span>{patient.phone_jsonb[0].number}</span>
                  </div>
                )}
                {(patient.building_street || patient.area) && (
                  <div className="flex items-center gap-2 text-sm text-gray-600">
                    <MapPin className="w-4 h-4" />
                    <span>
                      {[patient.building_street, patient.area, patient.city]
                        .filter(Boolean)
                        .join(", ")}
                    </span>
                  </div>
                )}
                {patient.google_maps_link && (
                  <a
                    href={patient.google_maps_link}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex items-center gap-2 text-sm text-blue-600 hover:underline"
                  >
                    <ExternalLink className="w-4 h-4" />
                    Open in Google Maps
                  </a>
                )}
              </div>
            )}
          </div>

          {/* Appointment Details */}
          <div className="bg-gray-50 rounded-lg p-4">
            <div className="flex items-center gap-2 mb-3">
              <Calendar className="w-5 h-5 text-gray-600" />
              <h3 className="text-lg font-semibold">Appointment Details</h3>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <p className="text-sm text-gray-600">Date</p>
                <p className="font-medium">{formatCrmDate(appointment.appointment_date)}</p>
              </div>
              <div>
                <p className="text-sm text-gray-600">Time</p>
                <div className="flex items-center gap-2">
                  <Clock className="w-4 h-4 text-gray-600" />
                  <p className="font-medium">
                    {startTimeStr && endTimeStr ? `${startTimeStr} - ${endTimeStr}` : "N/A"}
                  </p>
                </div>
              </div>
              <div>
                <p className="text-sm text-gray-600">Duration</p>
                <p className="font-medium">{appointment.duration_minutes} minutes</p>
              </div>
              <div>
                <p className="text-sm text-gray-600">Status</p>
                {status && (
                  <Badge
                    className={`${
                      status.value === "scheduled"
                        ? "bg-yellow-100 text-yellow-700"
                        : status.value === "cancelled"
                        ? "bg-red-100 text-red-700"
                        : status.value === "completed"
                        ? "bg-green-100 text-green-700"
                        : ""
                    }`}
                  >
                    {status.label}
                  </Badge>
                )}
              </div>
              <div>
                <p className="text-sm text-gray-600">Type</p>
                {appointmentTypesList.length > 0 ? (
                  <div className="flex flex-wrap gap-2 mt-1">
                    {appointmentTypesList.map((type) => (
                      <Badge
                        key={type.value}
                        style={{
                          backgroundColor: type.color + "20",
                          color: type.color,
                        }}
                      >
                        {type.label}
                      </Badge>
                    ))}
                  </div>
                ) : appointmentType ? (
                  <Badge
                    style={{
                      backgroundColor: appointmentType.color + "20",
                      color: appointmentType.color,
                    }}
                  >
                    {appointmentType.label}
                  </Badge>
                ) : (
                  <span className="text-sm text-gray-500">N/A</span>
                )}
              </div>
            </div>
          </div>

          {/* Staff Details */}
          <div className="bg-gray-50 rounded-lg p-4">
            <div className="flex items-center gap-2 mb-3">
              <Stethoscope className="w-5 h-5 text-gray-600" />
              <h3 className="text-lg font-semibold">Staff Details</h3>
            </div>
            <div className="space-y-2">
              {primaryStaff && (
                <div className="p-3 bg-white rounded-lg border">
                  <div className="flex items-center justify-between mb-2">
                    <p className="font-medium">{primaryStaff.first_name} {primaryStaff.last_name}</p>
                    <Badge className="bg-blue-100 text-blue-700">Primary</Badge>
                  </div>
                  <p className="text-sm text-gray-600">{primaryStaff.staff_type} {primaryStaff.specialization && `- ${primaryStaff.specialization}`}</p>
                  <div className="flex items-center gap-2 mt-2 text-sm text-gray-600">
                    <Phone className="w-4 h-4" />
                    <span>{primaryStaff.phone}</span>
                  </div>
                </div>
              )}
              {assignedStaff.length > 0 && (
                <>
                  {assignedStaff.map((assignedStaffMember) => {
                    // Skip if this staff member is already shown as primary or driver
                    if (assignedStaffMember.id === appointment.primary_staff_id || assignedStaffMember.id === appointment.driver_id) {
                      return null;
                    }
                    return (
                      <div key={assignedStaffMember.id} className="p-3 bg-white rounded-lg border">
                        <div className="flex items-center justify-between mb-2">
                          <p className="font-medium">{assignedStaffMember.first_name} {assignedStaffMember.last_name}</p>
                          <Badge className="bg-gray-100 text-gray-700">Staff</Badge>
                        </div>
                        <p className="text-sm text-gray-600">{assignedStaffMember.staff_type} {assignedStaffMember.specialization && `- ${assignedStaffMember.specialization}`}</p>
                        <div className="flex items-center gap-2 mt-2 text-sm text-gray-600">
                          <Phone className="w-4 h-4" />
                          <span>{assignedStaffMember.phone}</span>
                        </div>
                      </div>
                    );
                  })}
                </>
              )}
              {driver && (
                <div className="p-3 bg-white rounded-lg border">
                  <div className="flex items-center justify-between mb-2">
                    <p className="font-medium">{driver.first_name} {driver.last_name}</p>
                    <Badge className="bg-green-100 text-green-700 flex items-center gap-1">
                      <Car className="w-3 h-3" />
                      Driver
                    </Badge>
                  </div>
                  <p className="text-sm text-gray-600">{driver.staff_type}</p>
                  <div className="flex items-center gap-2 mt-2 text-sm text-gray-600">
                    <Phone className="w-4 h-4" />
                    <span>{driver.phone}</span>
                  </div>
                </div>
              )}
              {!primaryStaff && assignedStaff.length === 0 && !driver && (
                <p className="text-sm text-gray-500 italic">No staff assigned</p>
              )}
            </div>
          </div>

          {/* Transportation */}
          <div className="bg-gray-50 rounded-lg p-4">
            <div className="flex items-center gap-2 mb-3">
              <Car className="w-5 h-5 text-gray-600" />
              <h3 className="text-lg font-semibold">Transportation</h3>
            </div>
            {driver ? (
              <p className="text-sm">{driver.first_name} {driver.last_name} - {driver.phone}</p>
            ) : (
              <p className="text-sm text-gray-600">Self Transport</p>
            )}
          </div>

          {/* Payment Package */}
          {paymentPackage && (
            <div className="bg-gray-50 rounded-lg p-4">
              <div className="flex items-center gap-2 mb-3">
                <CreditCard className="w-5 h-5 text-gray-600" />
                <h3 className="text-lg font-semibold">Payment Package</h3>
              </div>
              <div className="space-y-2">
                <div className="flex items-center justify-between">
                  <div>
                    {services && paymentPackage.service_id && (
                      <p className="font-medium">
                        {services.find((s) => s.id === paymentPackage.service_id)?.name || "Service"} Package #{paymentPackage.id}
