      `}</style>
      <div className="bg-white dark:bg-slate-800 flex flex-col overflow-hidden h-full min-h-0 w-full">

      {/* FullCalendar */}
      <div 
        className="overflow-y-auto flex-1 min-h-[750px]"
        style={{ 
          flex: "1 1 auto"
        }}
      >
        {typeof window !== "undefined" && (
          <FullCalendar
            ref={calendarRef}
            plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin, listPlugin]}
          initialView={fullCalendarView}
          initialDate={convertCrmDateToFullCalendarDate(currentDate)}
          timeZone={getCrmTimeZone()}
          firstDay={1}
          slotDuration="00:15:00"
          slotLabelInterval="01:00:00"
          slotMinTime="00:00:00"
          slotMaxTime="24:00:00"
          nowIndicator={true}
          editable={true}
          selectable={true}
          selectMirror={true}
          events={events}
          eventClick={handleEventClick}
          select={handleDateSelect}
          eventDrop={handleEventDrop}
          eventResize={handleEventResize}
          datesSet={handleDatesSet}
          height="100%"
          headerToolbar={false}
          locale="en-GB"
          fixedWeekCount={false}
          showNonCurrentDates={true}
          dayHeaderFormat={(arg: any) => {
            try {
              // Get view type from calendar API if available, otherwise use state
              const calendarApi = calendarRef.current?.getApi();
              const viewType = calendarApi?.view?.type || currentView;

              // Extract date from arg - FullCalendar v6 date/start are objects with marker property
              let date: Date | null = null;

              // Try arg.date.marker (most reliable)
              if (arg?.date?.marker) {
                date = new Date(arg.date.marker);
              } else if (arg?.start?.marker) {
                date = new Date(arg.start.marker);
              } else if (arg?.date instanceof Date) {
                date = arg.date;
              } else if (arg?.start instanceof Date) {
                date = arg.start;
              } else if (typeof arg?.date === "string") {
                date = new Date(arg.date);
              } else if (typeof arg?.start === "string") {
                date = new Date(arg.start);
              } else if (arg?.marker) {
                date = new Date(arg.marker);
              }

              if (date && !isNaN(date.getTime())) {
                // For month view column headers, show day name (Mon, Tue, Wed, etc.)
                if (viewType === "dayGridMonth") {
                  return format(date, "EEE");
                }
                // For week/day views, show weekday and date like "Thu 25 Dec"
                if (viewType === "timeGridWeek" || viewType === "timeGridDay" || viewType === "dayGridWeek") {
                  const weekday = format(date, "EEE");
                  const day = date.getDate();
                  const month = format(date, "MMM");
                  return `${weekday} ${day} ${month}`;
                }
                // For list view, show full format like "Thursday 25th December"
                if (viewType === "listWeek" || viewType === "listDay" || viewType === "listMonth") {
                  const weekday = format(date, "EEEE");
                  const day = date.getDate();
                  const suffix = day === 1 || day === 21 || day === 31 ? "st" :
                                day === 2 || day === 22 ? "nd" :
                                day === 3 || day === 23 ? "rd" : "th";
                  const month = format(date, "MMMM");
                  return `${weekday} ${day}${suffix} ${month}`;
                }
                // Fallback for other views
                const weekday = format(date, "EEE");
                const day = date.getDate();
                const month = format(date, "MMM");
                return `${weekday} ${day} ${month}`;
              }

              // Debug: log what we're getting
              console.log("dayHeaderFormat - no valid date:", { 
                arg, 
                dateObj: arg?.date, 
                startObj: arg?.start,
                viewType, 
                currentView 
              });

              // Let FullCalendar use default
              return undefined as any;
            } catch (error) {
              console.error("dayHeaderFormat error:", error, arg);
              // Silently fail and let FullCalendar use default
              return undefined as any;
            }
          }}
          slotLabelFormat={{
            hour: "2-digit",
            minute: "2-digit",
            hour12: false,
          }}
          eventContent={(eventInfo) => {
            const appointment = eventInfo.event.extendedProps.appointment as Appointment;
            const view = eventInfo.view;
            const isMonthView = view.type === 'dayGridMonth';

            // First, check if we have selected service IDs stored in custom_fields
            // Declare once at the top to use in both month and week/day/list views
            const selectedServiceIds = appointment?.custom_fields?.selected_service_ids;

            // Get typeColor from selected service - use selected_service_ids from custom_fields if available
            // This ensures we use the exact service color set in Services Management
            let typeColor = "#a855f7"; // Default purple
            if (Array.isArray(selectedServiceIds) && selectedServiceIds.length > 0) {
              // Use the first selected service's color
              const firstServiceId = selectedServiceIds[0];
              const selectedServiceType = appointmentTypes.find((t) => {
                const serviceType = t as any;
                // Compare as numbers to handle string/number mismatch
                return serviceType.serviceId?.toString() === firstServiceId?.toString() ||
                       serviceType.serviceId === firstServiceId ||
                       serviceType.serviceId === Number(firstServiceId);
              });
              if (selectedServiceType?.color) {
                typeColor = selectedServiceType.color;
              } else {
                console.warn("Service type not found for selected service ID:", {
                  appointmentId: appointment.id,
                  selectedServiceIds,
                  firstServiceId,
                  availableServiceIds: appointmentTypes.map(t => ({ 
                    serviceId: (t as any).serviceId, 
                    label: t.label,
                    color: t.color 
                  }))
                });
              }
            } else {
              // Fallback: Match by appointment_type to find the service
              const appointmentType = appointmentTypes.find(
                (type) => {
                  // Services are stored with value="service_${id}" and appointmentType="doctor_on_call" etc.
                  const serviceType = type as any;
                  if (serviceType.appointmentType === appointment.appointment_type) {
                    return true;
                  }
                  // Also check direct value match (for backwards compatibility)
                  if (type.value === appointment.appointment_type) {
                    return true;
                  }
                  return false;
                }
              );

              // Use color from service database (set in Services Management)
              if (appointmentType?.color && appointmentType.color !== "#6b7280" && appointmentType.color !== "") {
                typeColor = appointmentType.color;
              }
            }

            // For month view, show a more compact but readable format
            if (isMonthView) {
              // Use the original appointment start_time directly to avoid double timezone conversion
              // The event's start date has been adjusted for FullCalendar display, but we want
              // the actual appointment time as stored in the database
              const startDate = appointment.start_time 
                ? (typeof appointment.start_time === 'string' 
                    ? new Date(appointment.start_time) 
                    : appointment.start_time instanceof Date
                    ? appointment.start_time
                    : new Date(appointment.start_time))
                : null;
              const timeStr = startDate ? formatCrmTime(startDate) : '';

              // Get patient name (first part of title, limit length for readability)
              const titleParts = eventInfo.event.title.split(' - ');
              let patientName = titleParts[0] || eventInfo.event.title;
              // Truncate if too long
              if (patientName.length > 20) {
                patientName = patientName.substring(0, 17) + '...';
              }

              return (
                <div
                  onContextMenu={(e) => {
                    e.preventDefault();
                    handleEventRightClick(e, appointment);
                  }}
                  className="px-0 py-1 w-full h-full"
                  style={{
                    minHeight: '24px',
                    display: 'flex',
                    alignItems: 'stretch',
                  }}
                >
                  {/* Left colored section for appointment type - wider and more visible */}
                  <div
                    style={{
                      width: '8px',
                      backgroundColor: typeColor,
                      flexShrink: 0,
                      borderRadius: '4px 0 0 4px',
                      opacity: 1,
                    }}
                  />
                  {/* Content section */}
                  <div
                    className="px-2 flex items-center gap-1.5 flex-1"
                    style={{
                      color: '#ffffff',
                      fontSize: '11px',
                      fontWeight: '600',
                      lineHeight: '1.3',
                      textShadow: '0 1px 3px rgba(0,0,0,0.8), 0 0 2px rgba(0,0,0,0.5)',
                      whiteSpace: 'nowrap',
                      overflow: 'hidden',
                    }}
                  >
                    {timeStr && <span className="font-bold flex-shrink-0">{timeStr}</span>}
                    <span className="truncate">{patientName}</span>
                  </div>
                </div>
              );
            }

            // For week/day/list views, show full details with type color indicator
            // Get typeColor from selected service - use selected_service_ids from custom_fields if available
            let typeColorForView = "#a855f7"; // Default purple

            // Reuse selectedServiceIds already declared at the top of the function
            if (Array.isArray(selectedServiceIds) && selectedServiceIds.length > 0) {
              // Use the first selected service's color
              const firstServiceId = selectedServiceIds[0];
              const selectedServiceType = appointmentTypes.find((t) => {
                const serviceType = t as any;
                // Compare as numbers to handle string/number mismatch
                return serviceType.serviceId?.toString() === firstServiceId?.toString() ||
                       serviceType.serviceId === firstServiceId ||
                       serviceType.serviceId === Number(firstServiceId);
              });
              if (selectedServiceType?.color) {
                typeColorForView = selectedServiceType.color;
              }
            } else {
              // Fallback: Use color from extendedProps or find by appointment_type
              typeColorForView = eventInfo.event.extendedProps.typeColor;
              if (!typeColorForView || typeColorForView === "#6b7280" || typeColorForView === "") {
                const appointmentType = appointmentTypes.find(
                  (type) => {
                    if (type.value === appointment.appointment_type) return true;
                    const serviceType = type as any;
                    if (type.value?.startsWith("service_") && serviceType.appointmentType === appointment.appointment_type) {
                      return true;
                    }
                    return false;
                  }
                );
                // Use color from service database field
                typeColorForView = appointmentType?.color || "#a855f7";
              }
            }

            return (
              <div
                onContextMenu={(e) => {
                  e.preventDefault();
                  handleEventRightClick(e, appointment);
                }}
                className="px-0 py-1 w-full h-full"
                style={{
                  display: 'flex',
                  alignItems: 'stretch',
                }}
              >
                {/* Left colored section for appointment type - wider and more visible */}
                <div
                  style={{
                    width: '8px',
                    backgroundColor: typeColorForView,
                    flexShrink: 0,
                    borderRadius: '4px 0 0 4px',
                    opacity: 1,
                  }}
                />
                {/* Content section */}
                <div
                  className="px-2 flex items-center flex-1"
                  style={{
                    color: '#ffffff',
                    fontSize: '11px',
                    fontWeight: '600',
                    textShadow: '0 1px 2px rgba(0,0,0,0.5)',
                  }}
                >
                  {eventInfo.event.title}
                </div>
              </div>
            );
          }}
          />
        )}
      </div>
      </div>
    </>
  );
};

